style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
  .score{
    display:none;
  }
  .score.likes{
    display:none !important;
  }
  .score.dislikes{
    display:none !important;
  }
  .res_post_ups{
    display:none !important;
  }
  .res_post_downs{
    display:none !important;
  }
  .userkarma{
    display:none !important;
  }
  .karma{
    display:none !important;
  }
  .res_comment_ups{
    display:none !important;
  }
  .res_comment_downs{
    display:none !important;
  }
  .downvotes{
    display:none !important;
  }
  .upvotes{
    display:none !important;
  }
  /* This hides the karma when you hover over a name with RES */
  /*.authorDetail{
