style.css:
@-moz-document domain("facebook.com") {
  .userContent .text_exposed_root>p:first-of-type, .userContent>p:first-of-type {
    margin-top: auto !important;
  }
  .userContent>p:last-of-type, .userContent .text_exposed_show>p:last-of-type {
    margin-bottom: auto !important;
  }
  .userContent p {
    margin-top: 1em !important;
    margin-bottom: 1em !important;
  }
}
