style.css:
@-moz-document url-prefix("https://www.wowhead.com/") {
.news-list-wrapper > div[data-pinned="true"],
    #sticky-news-heading,
    .news-content-spotlight,
    #homepage-recent-posts,
    .header-user-lang,
    .header-user-feedback,
    .header-user-notifications,
    .header-user-fullscreen,
    .header-expand-site-tab,
    .header-nav-wrapper,
    .footer-social,
    .comment-tabs-block > div.block-bg:nth-child(1),
    .vertical-bg,
    a.header-expandsite,
    .blocks,
    #news-block-group,
    .announcement-inner,
    .premium-promo-row,
    .sidebar-wrapper {
        display: none!important;
    }

    .news-right > div:nth-child(1) {
        margin-bottom: 0!important;
    }

    .layout-inner {
        padding-left: 10px!important;
        padding-right: 10px!important;
    }

    footer .footer-zam > div {
        width: 33%;
    }

    footer .footer-zam > div.block-content:nth-child(3) {
        width: 34%;
    }

    .wrapper,
    .comment-tabs,
    .comment-tabs-body,
    .header .header-right {
        margin-right: 0 !important;
    }

    .page-content {
        padding-right: 0px !important;
    }
    .sidebar {
        padding: 0px !important;
    }
    .is-atf {
        display: none!important;
    }

    .zaf-unit-wrapper {
        display: none!important;
    }

    .header-wrapper {
        border-bottom: 1px solid #444;
    }
    .header-wrapper::before,
    .header-wrapper::after {
        content: unset
    }
    body[data-theme-type="standard"]:not(.no-header) .page-content {
        border-top-width: 90px;
    }
}
