style.css:
@-moz-document domain("boost.org") {
body, #body {
	background: #212121 !important;
	color: #b8b8b8 !important;
}

::selection {
  background: #4183C4 !important;
  color: #fff !important;
}
::-moz-selection {
  background: #4183C4 !important;
  color: #fff !important;
}

.exampleblock>.content,
select, input, textarea {
  color: #eee !important;
  background: #333;
  border-color: #555;
}


a, a .keyword, a .identifier, a .special, a .preprocessor a .char, a .comment, a .string, a .number {
	color: rgba(94,122,181,1.0) !important;
}

a:visited, a:visited .keyword, a:visited .identifier, a:visited .special, a:visited .preprocessor a:visited .char, a:visited .comment, a:visited .string, a:visited .number {
	color: #874e87 !important;
}

a:hover {
	color: #5c8759 !important;
}

