@-moz-document url-prefix("https://svn.boost.org/trac/"), url-prefix("http://svn.boost.org/trac/") {
h2.report-result {
	background-color: inherit !important;
}

table.listing thead {
	background-color: rgba(99,99,99,0.3) !important;
}
table.listing tbody tr.odd {
	background-color: inherit !important;
}
table.listing tbody tr.even {
	background-color: rgba(99,99,99,0.2) !important;
}

table.listing tbody tr {
  	border-top-width: 0 !important;
}


.collapsed > .foldable :link, .collapsed > .foldable :visited {
  	-webkit-filter: invert(0.87);
}
      
#ticket {
    background-color: rgba(80%, 80%, 60%, 0.1) !important;
}
#comment {    
	border: 1px solid rgba(100%,100%,100%,0.07) !important;
	background-color: #2b2b2b !important;
}
.ticketdraft {
	filter: invert(0.87);
	-webkit-filter: invert(0.87);
}

/*#mainnav :link, #mainnav :visited,
#prefs fieldset legend,
a.missing:hover,*/
#header h1 :link:hover, #header h1 :visited:hover { color: inherit !important }
table.listing tbody tr[style*="background: #ddd"]:not(:hover) {
    background-color: #111 !important;
}
table.listing tbody tr:hover { background: #332 !important }

.tickets tbody tr.fullrow td,
.tickets tbody td.fullrow {
    color: #bbb !important;
}

.tickets .fullrow .meta {
    color: #ccc !important;
}

table.listing tbody td,
table.listing tbody th {
	border-color: #444;
}
}

