@-moz-document domain("m.prohardver.hu"), domain("m.itcafe.hu"), domain("m.gamepod.hu"), domain("m.mobilarena.hu"), domain("m.logout.hu") {

/*
ver. 0.8.5 by sz.balazs.95
created: 2015.10.29
published: 2015.11.01

img-blue: #29007f;
txt-blue: #00c0ff;
light gray: #939598;
gray: #58595B;
dark gray: #333333;
*/

#top {display: none}
.anyagok .blog h1 a, .anyagok span.time {color: #000}
a {color: #00c0ff;font-weight: bold}
.anyagok span.msgs a {background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.4a/msg-icon-1.1.png) no-repeat scroll 0px 0.5em;color: #000}
.anyagok span.type, .anyagok .blog span.type {color: #fff;background-color: #58595b}
.anyagok .feat span.type {color: #fff;background-color: #29007f}
.anyagok .feat h1 a {color: #00c0ff}
.anyagok p, .msgblk .text p {color: #000}
.anyag h2 {color: #58595b}
.anyagok li {background: #f3f3f3}
body{background-color: #f3f3f3}
h6 a {color: #00c0ff}
h2 {background-color: #939598}
#search input[type="text"] {border: 2px solid #58595b;background-color: #f3f3f3}
#navi a, #navi .grey a {border: 2px solid #58595b;background-color: #939598;color: #000}
#navi .act a, #search input {border: 2px solid #29007f;background-color: #939598;color: #000}
#network ul li a {color: #000}
.forums li.ma a, .threads li.feat a {color: #00c0ff}
#main .header h4 {background-color: #58595b}
.relcnt h4 {color: #000;background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.4a/blackline-1.1.png) repeat-x scroll 0% 20px}
.relcnt li {background: #f3f3f3}
.threads li.odd {background-color: #f3f3f3}
.threads li.evn {background-color: #939598}
.threads li p a {color: #00c0ff}
.thrnavi {background-color: #000}
.msgblk .head {background-color: #29007f;border-color: #29007f}
.msgblk a {color: #00c0ff}
#foot {background: #58595b url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.4a/pat-line-1.0.png)}
.forums li.title {background: #f3f3f3 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.4a/pat-line-1.0.png);text-align: center}
