options.value:
/*Logo Text Color*/
.gb_Zc, .NkK3Fc span[role="heading"]{
    color: var(--gbar-text-color) !important;
}

/*Coloring for svg icons in new Google Bar*/
.gb_ja svg{
	fill: var(--gbar-text-color) !important;
	color: var(--gbar-button-color-2) !important;
}

/*Remove right border when selecting a button in the Google Bar*/
.gb_g{
    border-right: 0px solid var(--gbar-border-color) !important;
}

/*========================Apps Button Popup========================*/
/*Triangle icon at top when popup is opened*/
.gb_Qa{
    border-bottom-color: var(--gbar-light-background-color) !important;
    z-index: 3 !important;
}

/*Main background color*/
.gb_H{
    border: 0px solid var(--gbar-border-color-2) !important;
    background-color: var(--gbar-light-background-color) !important;
}

/*Individual apps button text color*/
.gb_r{
    color: var(--gbar-button-color-2) !important;
    border: none !important;
}

/*Hides the border when hovering over an individual app icon*/
.gb_i:hover a{
    border: none !important;
}

/*Text color when hovering over an individual app icon*/
.gb_r:hover{
    color: var(--gbar-border-color-2) !important;
    text-decoration: underline !important;
}

/*Background color behind app label*/
.gb_i:hover .gb_r{
    background: var(--gbar-light-background-color) !important;
}

/*Text color for the service in the box to add a new shortcut*/
.gb_V{
    color: var(--gbar-border-color-2) !important;
}

/*Shadow around the box to add a new shortcut*/
.gb_U{
    box-shadow: var(--gbar-box-shadow2) !important;
}

/*Background color for "More" link at bottom of popup*/
.gb_1f, .gb_I{
    background-color: var(--gbar-light-background-color) !important;
    color: var(--gbar-border-color-2) !important;
}
.gb_I:hover{
    text-decoration: underline !important;
}

/*Small bottom border on the first page of apps that shows up when you scroll to the second page of apps*/
.gb_J{
    border-bottom: 0px solid var(--gbar-button-color-2) !important;
}

/*=========================User Popup=========================*/
/*Main Background color*/
.gb_D{
    background: var(--gbar-light-background-color) !important;
    box-shadow: var(--gbar-box-shadow2) !important;
}

/*Picture icon background*/
.gb_cb{
    background: var(--gbar-main-background-color) !important;
}
.gb_db:hover{
    background: var(--gbar-button-color-2) !important;
}

/*User name*/
.gb_gb{
    color: var(--gbar-border-color-2) !important;
}

/*Buttons background color*/
.gb_pb{
    background: var(--gbar-button-color) !important;
    border-color: var(--gbar-button-color) !important;
    color: var(--gbar-border-color-2) !important;
}
.gb_pb:hover{
    background: var(--gbar-main-background-color) !important;
}

/*Secondary user background*/
.gb_Kb:hover{
    background: var(--gbar-main-background-color) !important;
}

/*Secondary user text color*/
.gb_1b{
    color: var(--gbar-border-color-2) !important;
}

/*All Brand accounts background*/
.gb_sb{
    background: var(--gbar-light-background-color) !important;
    border-top: 0px solid var(--gbar-border-color) !important;
}
.gb_Ob:hover{
    background: var(--gbar-main-background-color) !important;
}

/*All Brand accounts text*/
.gb_cc, .gb_sb{
    color: var(--gbar-border-color-2) !important;
}

/*Add another account background*/
.gb_sb:hover, .gb_nb:hover{
    background: var(--gbar-main-background-color) !important;
}

/*Dividing Lines*/
.gb_Eb, .gb_yb{
    border-top: 0px solid var(--gbar-border-color) !important;
    border-bottom: 0px solid var(--gbar-border-color) !important;
}
