style.css:
@-moz-document domain(web.whatsapp.com) {


.message-text, .btn-more, .infinite-list-item .list-title, .input-container .input, .popup-title{
  color: /*[[Color]]*/ !important;
}

.app-wrapper::before,.app-wrapper{background:#3A3A3A !important;}
::-webkit-scrollbar-track{background: #414141 !important;}
::-webkit-scrollbar-thumb{background-color: /*[[Color]]*/ }
.btn-default {color: #cccccc; background-color: /*[[Color]]*/;}
header.pane-header {background-color: /*[[Color]]*/ !important;}
.btn-context{background-color: /*[[Color]]*/ !important;}
.message-out .btn-context:after {
    background: -webkit-linear-gradient(left,rgba(220,248,198,0),/*[[Color]]*/);
    background: linear-gradient(to right,rgba(220,248,198,0),/*[[Color]]*/);
}
.message-in .btn-context:after {
    background: -webkit-linear-gradient(left,rgba(255,255,255,0),/*[[Color]]*/);
    background: linear-gradient(to right,rgba(255,255,255,0),/*[[Color]]*/);
}
.drawer-header-popup {
    background-color: /*[[Color]]*/ !important;
}
.drawer .menu-tabs-lists {
    background-color: /*[[Color]]*/ !important;
}
.btn {
    background-color: /*[[Color]]*/ !important;
}
.drawer-header-offset {
    background-color: /*[[Color]]*/ !important;
}
.input-line {
  color: /*[[Color]]*/ !important;
}
.menu-tabs-emoji {
  background-color: /*[[Color]]*/ !important;
}
.media-caption .input-advanced:not(.active) {
    border-bottom-color: /*[[Color]]*/ !important;
}
.input-advanced.active {
    border-bottom-color: /*[[Color]]*/ !important;
}
.btn-fill {
    color: /*[[Color]]*/ !important;
}

.btn-default:hover {
    background-color: /*[[Color]]*/ !important;
}
.unread-count {
    background-color: /*[[Color]]*/ !important;
}
.typing {
    color: /*[[Color]]*/ !important;
}
.pane-chat-body {
    background: #FFCCFF!important;
}
}
