style.css:
@-moz-document domain("r4p3.net"), domain("rape.ru.com") {
/* Global dark style - changes everything to DARK (https://userstyles.org/styles/31267/)*/
html,
#newtab-window {	background:url("data:image/png;base64,") #222 !important;
}
/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
*:not(.username),*:not(.redactor_color_link),*:not(.userTitle),*:not(.userBanner) { background-color: transparent !important; }
* {
	color: #999;
	text-shadow: 0 0 3px #000 !important;
	box-shadow: none !important;
	background-color: transparent;
	border-color: #444 !important;
	border-top-color: #444 !important;
	border-bottom-color: #444 !important;
	border-left-color: #444 !important;
	border-right-color: #444 !important
}
body {
	background: transparent !important
}
*:before,
*:after {
	background-color: transparent !important;
	border-color: #444 !important
}
a,
a * {
	color: #409B9B !important;
	text-decoration: none !important
}
a:hover,
a:hover *,
a:visited:hover,
a:visited:hover *,
span[onclick]:hover,
div[onclick]:hover,
[role="link"]:hover,
[role="link"]:hover *,
[role="button"]:hover *,
[role="menuitem"]:hover,
[role="menuitem"]:hover *,
.link:hover,
.link:hover * {
	color: #F0F0F0 !important;
	text-shadow: 0 0 5px rgba(255, 255, 200, 0.9) !important
}
a:visited,
a:visited * {
	color: #607069 !important
}
a.highlight,
a.highlight *,
a.active,
a.active *,
.selected,
.selected *,
[href="#"] {
	color: #DDD !important;
	font-weight: bold !important
}
h1,
h2,
h3,
h4,
h5,
h6,
h1 *,
h2 *,
h3 *,
strong,
[id*="headline"],
[class*="headline"],
[id*="header"],
[class*="header"],
[class*="header"] td {
	color: #DDD !important
}
a h1,
a h2,
a h3,
a h4,
a h5,
a h6,
h1 a,
h2 a,
h3 a,
a strong,
a[id*="headline"],
a[class*="headline"],
a[id*="header"],
a[class*="header"] {
	text-decoration: underline !important
}
[class*="error"],
[class*="alert"],
code,
span[onclick],
div[onclick] {
	color: #900 !important
}
::-moz-selection {
	background: #377 !important;
	color: #000 !important
}
::selection {
	background: #377 !important;
	color: #000 !important
}
:focus {
	outline: none !important
}
/*----- MENU & CO BACKGROUND-COLORS -----*/

div[style="display: block;"],
div[role="navigation"] {
	background: rgba(0, 0, 0, .5) !important
}
table {
	background: rgba(40, 30, 30, .6) !important;
	border-radius: 6px !important
}
table > tbody > tr:nth-child(even),
table > tbody > tr > td:nth-child(even) {
	background-color: rgba(0, 0, 0, .2) !important
}
iframe,
embed,
label [onclick],
div[style*="position:"][style*="left:"][style*="visible"],
div[style*="z-index:"][style*="left:"][style*="visible"],
div[style*="-moz-user-select"],
div[role="menu"],
div[role="dialog"],
span[class*="script"] div,
xenOverlay,
[id*="menu"],
[class*="dropdown"],
[class*="popup"],
ul[style*="display:"],
ul[style*="visibility:"] ul,
ul[class*="menu"],
a[onclick][style*="display"],
a[id*="ghosteryfirefox"],
#ghostery-purple-bubble,
#translator-popup,
#jumpMenu,
#alertsMenu,
.menu,
.tooltip,
.hovercard,
.vbmenu_popup {
	background: rgba(5, 5, 5, .9) !important;
	border-radius: 5px;
	box-shadow: 1px 1px 5px #000 !important
}
[id*="overlay"],
[id*="lightbox"],
blockquote {
	background-color: rgba(35, 35, 35, .9) !important;
	border-radius: 5px
}
pre,
.Message code {
	background-color: rgba(5, 5, 5, .5) !important
}
/*----- CHANGE CURSORS -----*/

* {
	cursor: url("data:image/x-icon;base64,"), default !important
}
a,
a * {
	cursor: url("data:image/x-icon;base64,"), pointer !important
}
/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/

input,
select,
button,
[role="button"],
a.button,
a.submit,
a.BigButton,
a.TabLink,
.install[onclick] {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	transition: border-color 0.3s !important;
	background: #060606 !important;
	color: #BBB !important;
	text-shadow: 0 1px #000 !important;
	border: 2px solid #333 !important;
	border-radius: 4px !important;
	box-shadow: 0 0 2px rgba(0, 0, 0, .9) !important
}
a[href="javascript:;"],
a[class*="button"]:not(:empty),
a[id*="button"]:not(:empty),
a[id*="Button"]:not(:empty),
div[class*="button"][onclick] {
	transition: border-color 0.3s !important;
	background: #060606 !important;
	color: #BBB !important;
	text-shadow: 0 1px #000 !important;
	border-color: #333 !important;
	box-shadow: 0 0 2px rgba(0, 0, 0, .9) !important
}
a[href="javascript:;"]:hover,
a[class*="button"]:not(:empty):hover,
a[id*="button"]:hover,
a[id*="Button"]:not(:empty):hover,
div[class*="button"][onclick]:hover {
	background: #151515 !important;
	color: #FFF !important
}
input *,
select *,
button *,
a.button *,
a.submit * {
	color: #BBB !important;
	text-shadow: none !important
}
input:hover,
input[type="button"]:hover,
input[type="checkbox"]:hover,
input[type="radio"]:hover,
select:hover,
button:hover,
[role="button"]:hover,
a.button:hover,
a.submit:hover,
a.BigButton:hover,
a.TabLink:hover {
	border: 2px solid #555 !important;
	border-top-color: #555 !important;
	border-bottom-color: #555 !important;
	border-left-color: #555 !important;
	border-right-color: #555 !important
}
input:focus,
select:focus {
	box-shadow: 0 0 5px #077 !important
}
input *:hover * {
	color: #F0F0F0 !important;
	text-shadow: 0 0 2px #FFF !important
}
input[type="checkbox"],
input[type="checkbox"]:focus {
	border: 2px solid #333 !important;
	background-color: #111 !important;
	color: #DDD !important;
	border-radius: 4px !important
}
input[type="radio"],
input[type="radio"]:focus {
	border: 2px solid #333 !important;
	background-color: #111 !important;
	color: #DDD !important
}
input[type="checkbox"],
input[type="radio"] {
	min-width: 10px;
	min-height: 10px
}
select input[type="button"],
select button {
	border: none !important
}
select button {
	min-height: 8px !important
}
button:active,
input[type="submit"]:active,
input[type="button"]:active,
a.button:active,
a[class*="button"]:not(:empty):active,
a.submit:active,
a.BigButton:active,
a.TabLink:active,
.Active .TabLink {
	background: #292929 !important;
	color: #FFF !important
}
textarea {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	background: rgba(0, 0, 0, .3) !important;
	border-radius: 3px !important;
	border: 1px solid #000 !important;
	box-shadow: inset 0 0 8px #000 !important;
	transition: border-color, background, 0.3s !important
}
textarea,
textarea * {
	color: #C8C8C8 !important;
	text-shadow: 0 0 1px gray !important
}
textarea:hover,
textarea:focus:hover {
	border-color: #333 !important
}
textarea:focus {
	background: rgba(0, 0, 0, .5) !important;
	border-color: #222 !important
}
textarea:focus,
textarea:focus > * {
	text-shadow: none !important;
	box-shadow: none !important
}
option {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	background: none !important;
	color: #666 !important
}
option:not([disabled]):hover,
option:focus,
option:checked {
	background: linear-gradient(#333, #292929) !important;
	color: #DDD !important
}
/* webkit checkbox & select fix */

@media screen and (-webkit-min-device-pixel-ratio:0) {
	input[type="checkbox"]:checked, input[type="radio"]:checked {
		border-color: #DDD !important
	}
	select {
		padding-right: 12px !important;
		background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAKCAYAAABrGwT5AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAApSURBVChTYxjiYObMmf+hTBSASxwDoCskWiMMwDSQrBEGyNY4FAADAwDdpBOHzViE2AAAAABJRU5ErkJggg==) #000 no-repeat !important;
		background-position: right center !important
	}
}
/*----- SCROLLBAR CHANGES -----*/

scrollbarbutton {
	-moz-appearance: none !important;
	background-color: transparent;
	border: none !important
}
thumb {
	-moz-appearance: none !important;
	border: 2px solid transparent !important;
	background: #999 !important;
	background-clip: padding-box !important;
	border-radius: 3px !important;
	box-shadow: inset 0 0 3px #000 !important
}
scrollbar {
	-moz-appearance: none !important;
	background: transparent !important;
	box-shadow: inset #000 0 0 8px !important
}
scrollbarbutton:hover,
thumb:active,
thumb:hover {
	opacity: 0.5 !important
}
scrollbar[orient="vertical"] thumb {
	min-width: 12px !important;
	max-width: 12px !important;
	min-height: 14px !important
}
scrollbar[orient="horizontal"] thumb {
	min-height: 12px !important;
	max-height: 12px !important;
	min-width: 14px !important
}
scrollbar[orient="vertical"] {
	min-width: 12px !important;
	max-width: 12px !important
}
scrollbar[orient="horizontal"] {
	min-height: 12px !important;
	max-height: 12px !important
}
/*----- IMAGE CHANGES -----*/

body,
*:not(:empty):not(html):not(span):not(a):not(b):not(option):not(img):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([class*="icon"]):not(.text):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(#globalsearch):not(.sp),
.r3_hm,
.gmbutton2 b,
.gtab-i,
.ph,
.bstab-iLft,
.csb,
#pagination div,
[style*="sprite2.png"],
#mw-head-base,
#mw-page-base {
	background-image: none !important
}
img {
	opacity: .7 !important;
	transition: opacity .2s
}
img:hover,
a:hover img {
	opacity: 1 !important
}
}

