style.css:
@-moz-document domain("isup.me") {
body {
  background-color: #333;
  color: #bbb;
}

input {
  background-color: #444;
  border-color: #222 !important;
  color: #ccc !important;
}

input:focus {
  border-color: #4183C4 !important;
  box-shadow: 0px 1px 2px rgba(255, 255, 255, 0.075) inset,
              0px 0px 5px rgba(255, 255, 255, .075) !important;
}

}
