style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(.*\.)yp.ru"), domain("yp.ru") {
  #content {
   background-color: #fff;
  }
  
  #content #content__need-help, #content .yp-tgb-items-wrapper, #content #siteDistrictsWrapper, #content .yp-direct-wrapper, #content #index-frequent-rubrics, #content #index-allcompany-wrapper, #content #mp_worldNewsWrapper,
  #content #mp_companyNewsWrapper, #content #mp_discountsWrapper, #content #mp_infoTextWrapper, #yp-fullscreen {
   display: none;
  }
  
  #content, #content #search_form_wrapper {
   max-width: 100% !important;
  }
  
  #bread_crumbs_wrapper {
    background-color: #fff;
    border: none !important;
    padding: 16px;
  }
  
  .crumb_corner {
    display: none;
  }
}
