style.css:
@-moz-document domain("7cups.com") {
/* by shade */


  .navbar {
    background: #000 !important;
    background-image: none;
  }
  body, .container-fluid, .hidden-sm-down
  {
    background-color:#17191A !important;
  }
  #bottomStatus, .bottomStatus
  {
    color:#fff !important;
    background-color:#089FD1 !important;
  }
  #bottomStatus.heavyLoad {
    color: #fff !important;
    background-color: #089FD1 !important;
    background: #089FD1 !important;
  }
  .btn-default, 
  .btn-primary
  {
    color:#fff !important;
    background-color:#17191A !important;
  }
  .card .nav-link, .active
  {
    color:#fff !important;
    background-color:#2B2F30 !important;
  }
  .card
  {
    background-color:#2B2F30 !important;
  }
  .convHolder, #actionButtons, #actionButtons img
  {
    background-color:#2B2F30 !important;
  }
  .convHolder .note
  {
    color:#FFFFFF !important;
  }
  .card-title
  {
    color:#ffffff !important;
  }
  textarea
  {
    background-color:#2B2F30 !important;
    color:#fff !important;
  }
  .youWrap, .meWrap
  {
    color:#000 !important;
  }
  .text-center, .details
  {
    color:#fff !important;
  }
  #MessageScrollDivResize, #MessageScrollDiv
  {
    border:none !important;
  }
  .modal-content
  {
    background-color:#2B2F30 !important;
  }
  .modal-content a
  {
    color:#fff !important;
  }
  .modal-title
  {
    color:#fff !important;
    background: inherit !important;
  }
  .listenerData
  {
    color:#fff !important;
  }
  table .listenerData .data
  {
    color:#fff !important;
  }
.userScreenName { color: #f0f8ff }
.userImage { border-radius: 10%; }
}
