style.css:
@-moz-document domain("7cups.com") {
body {
    background-color: #FF87C3 !important;
}
.navbar {
    background: #CC5490 !important;
    background-image: none;
  }
#bottomStatus,
.bottomStatus {
    color: #fff !important;
    background-color: #FF87C3 !important;
}
#bottomStatus.heavyLoad {
    color: #577A3A !important;
    background-color: #CC5490 !important;
    background: #CC5490 !important;
}
.btn-default,
.btn-primary {
    color: #000000 !important;
    background-color: #FF87C3 !important;
}
.card .nav-link,
.active {
    color: #CC5490 !important;
    background-color: #fefefe !important;
}
.card {
    background-color: #fefefe !important;
}
.convHolder,
#actionButtons,
#actionButtons img {
    background-color: #fefefe !important;
}
.convHolder .note {
    color: #CC5490 !important;
}
.card-title {
    color: #CC5490 !important;
}
textarea {
    background-color: #fefefe !important;
    color: #CC5490 !important;
}
.chatBox .youWrap {
    background-color: #FF87C3;
}
.chatBox .meWrap {
    color: #000000 !important;
    background-color: #FF87C3;
}
.text-center,
.details {
    color: #CC5490 !important;
}
.userImage { border-radius: 10%; 
}
