style.css:
@-moz-document domain("7cups.com") {
body {
    background-color: #E3B8FF !important;
}
.navbar {
    background: #CC00FF !important;
    background-image: none;
  }
#bottomStatus,
.bottomStatus {
    color: #fff !important;
    background-color: #C2F0FF !important;
}
.btn-default,
.btn-primary {
    color: #000000 !important;
    background-color: #FFFF99 !important;
}
.card .nav-link,
.active {
    color: #9900FF !important;
    background-color: #FFFFFF !important;
}
.card {
    background-color: #FFFFFF !important;
}
.convHolder,
#actionButtons,
#actionButtons img {
    background-color: #FFFFFF !important;
}
.convHolder .note {
    color: #9900FF !important;
}
.card-title {
    color: #191975 !important;
}
textarea {
    background-color: #FFFFFF !important;
    color: #0000E6 !important;
}
.chatBox .youWrap {
    background-color: #D6D6FF;
}
.chatBox .meWrap {
    color: #000000 !important;
    background-color: #99CCFF;
}
.text-center,
.details {
    color: #fff !important;
}
#bottomStatus,
.bottomStatus {
    color: #CC00FF !important;
    background-color: #CC00FF !important;
}
#bottomStatus.heavyLoad {
    color: #CC00FF !important;
    background-color: #CC00FF !important;
    background: #CC00FF!important;
}
}
