style.css:
.tabs-newtab-button {display:inline !important;}

#go-button { display: none !important }

[id^="context-stumble"] { display:none !important;}

#editBMPanel_keywordRow {
visibility: visible !important;
-moz-box-align: center !important;
}
#editBMPanel_tagsSelector[collapsed="true"] {
display: none !important;
}
#editBMPanel_tagsRow, #editBMPanel_locationRow, #editBMPanel_folderRow {
display: none !important;
}

#bookmarksPanel > hbox { display:none; }

#urlbar > dropmarker { display: none !important; }
#urlbar {background-color: -moz-rgba(255,255,255,1) !important;}
#urlbar {font-weight: bolder !important;}
#urlbar {border: 0px !important}
#urlbar {-moz-border-radius-topleft:5px !important;
		-moz-border-radius-topright:5px !important;
-moz-border-radius-bottomleft:5px !important;
-moz-border-radius-bottomright:5px !important;}

.tabbrowser-arrowscrollbox + stack {
display:none!important;
}

#identity-icon-label {
display: none !important;
} 

a:active
{
  outline: none;
}

.tab-text {
  border: none !important;
}

:focus
{
  -moz-outline-style: none;
}

:-moz-any-link:focus {
  outline: none;
}



#identity-box[tooltiptext="This web site does not supply identity information."]

{

  display:none;

}

#stop-button[disabled] {    
display: none;}

#stop-button:not([disabled]) + #reload-button {
display: none;
}



.tabs-closebutton-box, .tab-close-button{
display:none!important
}

.ac-type-icon, #star-button {
 display: none !important;
}


menuitem[label="Web Search"] { display: none; }

menuitem[label="Web Search"] + menuseparator { display: none; }



menuitem[label="Read Mail (0 new)"] { display: none; }

menuitem[label="Read Mail (0 new)"] + menuseparator { display: none; }

menuitem[label="New Message..."] { display: none; }

menuitem[label="New Message..."] + menuseparator { display: none; }
