style.css:
@-moz-document url("http://redsky.fr/fun/redsky-chat/") {
::-webkit-scrollbar-track {
  background-color: /*[[bgcolor1]]*/ !important;
}
::-webkit-scrollbar {
  width: 20px !important;
  background-color: /*[[bgcolor2]]*/ !important;
}
::-webkit-scrollbar-thumb {
  background-color: /*[[bgcolor2]]*/ !important;
}
body {
  background-color: /*[[bgcolor1]]*/ !important;
  color: /*[[textcolor1]]*/;
  background-image: url(/*[[bgimg1]]*/);
}
textarea,
input {
  outline: none;
}
.navbar,
div#ariane,
iframe[src="/ajax/fun/chat/img.php"],
footer,
input#skychat_action_submit {
  display: none;
}
.page-title {
  color: /*[[textcolor1]]*/;
}
.skychat_one_message,
.skychat_room_link {
  background-color: /*[[textcolor1]]*/ !important;
  transition: 2s all;
}
.skychat_one_message:hover {
  border-left: 6px solid /*[[bgcolor1]]*/ !important;
  transition: 0.8s all;
}
.skychat div {
  padding: 0;
}
.skyContent {
  margin-top: 20px !important;
}
.skychat_room_link {
  padding: 8px !important;
  border-color: #333 !important;
  margin-right: 8px;
}
p,
.skychat_date,
.skychat_message,
.skychat_message > a,
.skychat_one_message_green,
.blockquote-jv,
.blockquote-jv > span,
.skychat_one_message,
.skychat_one_message a,
.skychat_col_header > b,
div#skychat_connected_list,
div#skychat_connected_list span,
.skychat_room_link > a,
input#skychat_action_message,
#skychat_typing_list,
#skychat_typing_list > b,
a#skychat_action_message_music_toggle,
div#stream_loader > a,
b[style="color:#000"] {
  color: /*[[textcolor1]]*/ !important;
}
b[style="color:red"] {
  color: #e74c3c !important;
}
.skychat_one_message_red {
  border-left: 6px solid #c0392b !important;
}
.skychat_one_message_000 > a > b {
  color: #DDDDDD !important;
}
b[style="color:green"] {
  color: #27ae60 !important;
}
.skychat_one_message_green {
  border-left: 6px solid #27ae60 !important;
}
div#skychat_action_message_container {
  width: 100%;
}
input#skychat_action_message {
  background-color: rgb(22, 22, 22) !important;
  border: 0;
  width: 100%;
  padding: 6px;
  transition: 0.8s all;
}
input#skychat_action_message::-webkit-input-placeholder {
  color: transparent;
}
input#skychat_action_message::-webkit-input-placeholder::before {
  color: rgba(220, 220, 220, 0.5);
  content: "Skychat - Dark Theme by CielRouge";
}
input#skychat_action_message:focus {
  background-color: rgb(35, 35, 35) !important;
  transition: 0.8s all;
}
div#skychat_buffer {
  padding-right: 4px;
}
div#skychat_buffer::-webkit-scrollbar-track {
  background-color: rgb(15, 15, 15);
}
div#skychat_buffer::-webkit-scrollbar {
  width: 20px;
  background-color: rgb(35, 35, 35);
}
div#skychat_buffer::-webkit-scrollbar-thumb {
  background-color: rgb(35, 35, 35);
}
div#skychat_col_right {
  float: left;
  background-color: rgba(35, 35, 35, 0.2) !important;
  padding-left: 20px;
  max-width: 250px;
}
