style.css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){

	.sd-tab{
		background-color: #5a5a5a;
		color: #ebebeb;
	}
	
	.speeddialThumb {
		background: #484848;
		margin:10px !important;
		padding:9px;
		cursor:pointer;
		-webkit-box-shadow:  1px 3px 3px 1px #191919;
		box-shadow:  1px 3px 3px 1px #191919;		
		border-radius: 2px;
		-moz-transition: margin 0.2s ease-in-out;
	}
	
	.speeddialThumb:hover {
	  background:#888989;
		-webkit-box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.2);
		box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.2);
	}

	.speeddial-top-middle, .speeddial-top-left,.speeddial-top-right, .speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right, .speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right {
		display:none;
	}
	
	#speeddialWindow, .sd-tabbox {
			  background: #484848;
        background-repeat: no-repeat;
        background-position: center center;
        background-size: auto 100%;	
	}
	
	.speeddial-container{
		margin:0 !important;
		border:0 !important;
		background-color:  #5a5a5a  !important;
	}
	
	.sd-tabs-left, .sd-tabs-right{
		border-bottom-color: blue;
		-moz-border-bottom-colors: #6F6F6F #8B8B8B;
	}
	
	.sd-tab{
		border-bottom: 1px solid #8B8B8B !important;
		border-color: -moz-use-text-color -moz-use-text-color #8B8B8B;
	}

}
