/*
 *  Numix toolbar theme
 */

#nav-bar {
    margin-top: -1px !important;
}

#PersonalToolbar ~ #TabsToolbar {
    padding-bottom: 0 !important;
    box-shadow: none !important;
}

#main-window #TabsToolbar ~ toolbar#nav-bar {
    margin-top: -1px !important;
    border-top: 1px solid transparent !important;
}

#TabsToolbar::after {
    border-bottom-color: transparent !important;
}

#TabsToolbar ~ #nav-bar {
    box-shadow: none !important;
}

#main-window:not([disablechrome]) #navigator-toolbox {
    border-style: none !important;
}

#main-window:not([disablechrome]) #navigator-toolbox::after {
    display: none !important;
}

#nav-bar {
    background: none !important;
}

#menubar, #toolbar, #nav-bar, #TabsToolbar > *{
    background-color: #2d2d2d !important;
}

#identity-box {
    background: #dbdbdb !important;
}

#urlbar {
    background: #dbdbdb !important;
}

.searchbar-textbox {
    background: #dbdbdb !important;
}

/* Numix style toolbar icons */

#main-window #navigator-toolbox :-moz-any(#ctraddon_back-button, #back-button, #ctraddon_forward-button, #forward-button, #ctraddon_reload-button, #reload-button, #ctraddon_stop-button, #stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button, #privatebrowsing-button, #open-file-button, #preferences-button, #history-panelmenu, #add-ons-button, #save-page-button, #nav-bar-overflow-button, #PanelUI-menu-button, #find-button, #developer-button, #characterencoding-button, #email-link-button) > .toolbarbutton-icon,
#main-window #navigator-toolbox #bookmarks-menu-button:not([starred="true"]),
#main-window #navigator-toolbox #bookmarks-menu-button .toolbarbutton-menubutton-dropmarker .dropmarker-icon {
    list-style-image: url(data:image/png;base64,) !important;
}


/* Numix style download indicator */

#downloads-indicator-notification {
    display: none !important;
}

#main-window #navigator-toolbox #downloads-button[indicator="true"]:not([attention="true"]) #downloads-indicator-icon {
    background-image: url(data:image/png;base64,) !important;
}

#main-window #navigator-toolbox #downloads-button[indicator="true"] #downloads-indicator-icon {
    background-image: url(data:image/png;base64,) !important;
}

#downloads-indicator-counter {
    color: #cbcbcb !important;
    font-weight: bold !important;
}

/*
 *  Numix tab browser theme
 */

/* Remove tab background effects */

#TabsToolbar  .tab-background-middle {
    margin: 0 !important;
    border-style: none !important;
    height: 28px !important;
    background: transparent !important;
}

#TabsToolbar  .tab-background-start,
#TabsToolbar  .tab-background-end {
    display: none !important;
}

#TabsToolbar .tab-background-middle {
    background: transparent !important;
}

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}

/* Remove new tab hover effect */

#TabsToolbar .tabs-newtab-button {
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    border-style: solid !important;
    border-color: transparent !important;
    background: rgba(255,255,255,0) !important;
    background-clip: padding-box !important;
    transition: all 0s !important;
    opacity: 1 !important;
}


/* Remove highlight from the tab toolbar, give it the right background color */

#TabsToolbar .tabbrowser-toolbox {
    background-image: none !important;
    background-color: #2d2d2d !important;
}

/* Hide favicons */

/*
#TabsToolbar .tab-icon-image {
    display: none !important;
}
*/

/* Greyscale favicons */

#TabsToolbar .tabbrowser-tab:not([selected]) .tab-icon-image,
#TabsToolbar .tabbrowser-tab:not([selected]) .tab-throbber {
    filter: grayscale(100%);
}

#TabsToolbar .tabbrowser-tab[selected="true"] .tab-icon-image,
#TabsToolbar .tabbrowser-tab[selected="true"] .tab-throbber {
    filter: grayscale(0%);
}

#TabsToolbar .tabbrowser-tab:hover:not([selected]) .tab-icon-image,
#TabsToolbar .tabbrowser-tab:hover:not([selected]) .tab-throbber {
   filter: grayscale(0%);
}

/* Style tabs according to the Dark Numix theme */

#TabsToolbar .tabbrowser-tab {
    margin: 0 !important;
    margin-right: -1px !important;
    text-shadow: none !important;
}

#TabsToolbar .tab-background {
    background-image: none !important;
    border-top: 2px transparent solid !important;
    margin: 0 !important;
}

#TabsToolbar .tab-background:-moz-lwtheme {
    opacity: 1 !important;
}

#TabsToolbar .tabbrowser-tab[selected="true"] .tab-background {
    background-color: #2d2d2d !important;
    border-top-color: #d64937 !important;
    box-shadow: 1px 0 0 0 #232323 inset, -1px 0 0 0 #232323 inset !important;
}

#TabsToolbar .tabbrowser-tab:first-child[selected="true"] .tab-background {
    box-shadow: -1px 0 0 0 #232323 inset !important;
}

#TabsToolbar .tabbrowser-tab:not(:first-child):not([selected="true"]) .tab-background {
    box-shadow: 1px 0 0 0 #232323 inset !important;
}

#TabsToolbar .tabbrowser-tab:last-child:not([selected="true"]) .tab-background {
    box-shadow: 1px 0 0 0 #232323 inset, -1px 0 0 0 #232323 inset !important;
}

#TabsToolbar .tabbrowser-arrowscrollbox > scrollbox {
    padding: 0 !important;
}

#TabsToolbar .contentTab0 {
    margin-top: -1px;
}

#TabsToolbar tab {
    color: #cbcbcb !important;
}

/* Replace the default close tab icon with a light grey one */

#TabsToolbar .tab-close-button {
    list-style-image: url(data:image/png;base64,) !important;
}

/* Replace the default new tab icon with a light grey one */

#TabsToolbar .tabs-newtab-button {
    list-style-image: url(data:image/png;base64,) !important;
}

#main-window #navigator-toolbox :-moz-any(#new-tab-button) {
    list-style-image: url(data:image/png;base64,) !important;
}

/* Replace the default tab bar controls with light grey ones */

#main-window #navigator-toolbox .scrollbutton-up,
#main-window #navigator-toolbox .scrollbutton-down {
    list-style-image: url(data:image/png;base64,) !important;
}

#main-window #navigator-toolbox #alltabs-button[type="menu"],
#main-window #navigator-toolbox .toolbarbutton-1 .dropmarker-icon {
    list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH3wsWEgQBIde/hAAAAFlJREFUGNN1y7EJg1AABNAnCtbJDH8Ws4qlOEhIaeVkN4MrmEYhEf/BNce7Bi881bN1GJLMNVFKeTfo8Uky3oAFE7R4YE2yn8V67O15+oN34AqXGviF/RV8AQzJHHw+0vMGAAAAAElFTkSuQmCC) !important;
}

/* Remove overflow indicators */

#tabbrowser-tabs .tabbrowser-arrowscrollbox .arrowscrollbox-overflow-start-indicator,
#tabbrowser-tabs .tabbrowser-arrowscrollbox .arrowscrollbox-overflow-end-indicator {
   background-image: none !important;
}
