style.css:
@-moz-document url-prefix("https://userstyles.org/") {

  /* menu mods */
  #sidebar-browse > A::after { content: "Browse and Buy" }
  #sidebar-login > A::after { content: "Log in If you dare"}
  #sidebar-my-account > A::after { content: "Log in If you dare"}
  #sidebar-help > A::after { content: "Help me, please!" }
  #sidebar-forum > A::after { content: "The Funny Farm" }
  
  #left-sidebar nav A {font-size: 0}
  #left-sidebar nav A::after {font-size: small}

  #stylish-description::after {
    content: "Guess who?: No idea?";
    display: block;
  }

  /* hidden meta mods */
  #user-id::after { content: "Surprise! It's Agent 86" }
  #style-id::after { content: "Classified" }

  #user-id:not(:hover), #user-id:hover::after,
  #style-id:not(:hover), #style-id:hover::after {font-size: 0}
  #user-id::before, #user-id::after,
  #style-id::before, #style-id::after {font-size: small}
  #user-id, #style-id {
    transition: font-size 2s step-end;
  }
  #user-id:hover, #style-id:hover {transition-duration: 0s}
  #user-id::after, #style-id::after {transition: inherit}

}
