style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#PanelUI-menu-button {
  background-image:url(chrome://branding/content/identity-icons-brand.svg) !important;
  list-style-image: none !important;
  background-repeat: no-repeat !important;
  background-position: center, center !important;
  padding:0 !important;
}

#PanelUI-menu-button .toolbarbutton-badge-stack {opacity: 0 !important;}

#PanelUI-menu-button {
  transform: scale(.75) !important;
  border-radius: 6px !important;
}

#PanelUI-menu-button:hover{box-shadow:0 0 3px rgba(0,0,0,.5) inset !important;}
#PanelUI-menu-button:hover:active {box-shadow:0 0 6px rgba(0,0,0,.75) inset !important;}
#PanelUI-menu-button[open="true"] {box-shadow:0 0 3px rgba(0,0,0,.6) inset !important;}

