style.css:
@-moz-document domain("kissmanga.com") {
body {
    background: #111!important;
    background-color: #111!important;
    font-family: 'Meiryo'!important;
    overflow-x: hidden;
    color:#ccc
}
html {
    background: #111!important;
    color: #ccc
}
::-webkit-scrollbar {
    width: 7px;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    -webkit-border-radius: 10px;
    border-radius: 2px;
}
::-webkit-scrollbar-thumb {
    background: #393939!important; 
}
#head h1 a.logo {
    display: none;
}
a {
    color: #9C7EC3;
    font-family: 'Meiryo'!important;
    transition: color 0.3s;
}
.divCloseBut {
    display: none!important;
}
#head h3 {
    float: none;
    position: absolute;
    right: 0;
}
#search {
    width: 450px;
    position: absolute!important;
    right: 20px;
    top: -43px;
}
#result_box {
    position: absolute;
    top: 45px;
    left: 97px;
    color: #FFF;
    background: rgba(53, 53, 53, 0.8);
    opacity: 1;
    font-family: Meiryo;
    font-size: 13px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
}
#topHolderBox > img {
    display: none;
}
#topHolderBox > span {
    display: none;
}
#topHolderBox {
    background: transparent;
    font-family: Meiryo;
    position: absolute;
    right: 360px;
    top: 0;
    text-align: right;
}
form#formSearch > div:nth-child(4) > a:nth-child(1) {
    background: #333;
    font-size: 11px;
    padding-right: 11px;
    padding-left: 1px;
    position: relative;
    left: 8px;
}
#search input.button {
    display: none;
}
form#formSearch {
    font-family: 'Meiryo';
}
#formSearch > div:nth-child(4) > a > img {
    display: none;
}
.boxDropDown {
    border: none;
    border-top-right-radius: 0px;
    border-top-left-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    background: #393939;
    font-weight: normal;
    color: #DADADA;
    padding-top: 4px;
    position: absolute;
    top: 15px;
    right: -10px;
    width: 140px!important;
}
#menu_box {
    position: relative;
    top: 5px;
    left: 149px!important;
    background: #393939;
    font-family: Meiryo;
    border-top: 1px solid #171717;
    border-left: 1px solid #171717;
    border-right: 1px solid #171717;
}
#menu_box a {
    line-height: 26px;
    font-weight: normal;
    font-size: 11px;
    border-bottom: 1px solid #171717;
    transition: 0.3s;
}
#menu_box a:hover {
    background: #332840;
}
select {
    background-color: #393939;
    color: #ccc;
    border: none;
    font: normal 13px 'Meiryo';
    height: 24px;
    bottom: -1px;
    position: relative;
}
input.text {
    margin: 0 0 0 0;
    border: none;
    background: #393939;
    font: normal 12px Meiryo;
    color: #ccc;
}
#navcontainer li a#currentTab {
    background: #332840;
}
#navcontainer li a#currentTab:hover {
    background: #332840!important;
}
#navcontainer a:link, #navcontainer a:visited {
    background: #171717;
    font-weight: normal;
    color: #e0e0e0;
    width: 110px;
    transition: 0.3s;
}
#navcontainer a:hover {
    background: #393939!important;
}
.bigChar {
    font: normal 27px Meiryo;
    color: #9c7ec3;
    letter-spacing: normal;
}
.details {
    font: normal 13px Meiryo;
    color: #B3B3B3;
    line-height: 20px;
}
#navbar {
    width: 100%;
    background: transparent;
    height: 50px;
}
#navsubbar {
    background: #332840;
}
#head h1 {
    margin: 24px 30px 0px 0px;
}
.banner {
    background: #171717;
    border: none;
}
.details a img {
    border: 1px solid #171717;
    transition: 0.2s;
}
.banner img:hover {
    border: 1px solid #9C7EC3;
}
.arrow-general {
    display: none;
}
.bigBarContainer {
    background: #171717;
    border: none;
    padding: 0px;
}
.barTitle {
    background: #332840;
    font: normal 13px/25px Meiryo;
    letter-spacing: 0px;
    color: #fff;
}
.scrollable_title {
    font: normal 16px/27px Meiryo;
    letter-spacing: normal;
}
.barContent {
    background: transparent;
}
a.prev, a.prev:hover {
    background: url(http://puu.sh/lLuRj/6f9dfc9701.png)!important;
}
a.next, a.next:hover {
    background: url(http://puu.sh/lLuPZ/20eba08fcf.png)!important;
}
a.prev.disabled {
    opacity: 0.3;
}
div.items img {
    border: 1px solid #171717;
    transition: 0.2s;
}
div.items img:hover {
    border: 1px solid #9C7EC3;
}
#tabmenucontainer li a.tabactive {
    background: #332840;
}
#tabmenucontainer a:link, #tabmenucontainer a:visited {
    background: #171717;
    color: #e0e0e0;
    transition: 0.3s;
}
#tabmenucontainer a:hover {
    background: #393939;
    line-height: 25px;
}
#tabmenucontainer a {
    line-height: 25px;
}
#subcontent {
    background: #171717;
    border: none;
}
#tab-newest > div > div > img {
    display: none;
}
.dotUnder {
    border-bottom: none;
    margin-right: 3px;
}
#subcontent div a img {
    border: 1px solid transparent;
    transition: 0.2s;
}
#subcontent div a img:hover {
    border: 1px solid #9C7EC3;
}
div.pagination ul.pager li.current {
    background: #393939;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    color: #9C7EC3;
}
div.pagination ul.pager li {
    background: #393939;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    color: #fff;
    font-weight: normal;
}
div.pagination ul.pager li a {
    color: #fff;
}
div.pagination ul.pager li a:hover, div.pagination ul.pager li a:active {
    margin: 0px;
    background: #525252;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    color: #fff;
}
.rightBox {
    padding: 0px;
    border: none;
    background: #171717;
}
.tooltip {
    background: rgba(0,0,0,0.9)!important;
    padding: 14px 0px 0px 14px!important;
}
.tooltip > div > .bigChar {
    font-size: 14px;
}
.chapterList td a:visited {
    color: #A2A2A2;
}
.chapterVisited {
    color: #A2A2A2;
}
.barContent.chapterList > div > div:nth-of-type(1) {
    display: none;
}
.barContent.chapterList > div > div:nth-of-type(2) {
    display: none;
}
.barContent.chapterList > div > div:nth-of-type(3) {
    display: none;
}
.barContent.chapterList > div > div:nth-of-type(4) {
    display: none;
}
.barContent.chapterList > div > div:nth-of-type(5) {
    display: none;
}
.barContent.chapterList > div > div:nth-of-type(6) {
    display: none;
}
.listing th {
    border-bottom: 1px solid #1D1D1D;
}
#leftside > .bigBarContainer:nth-child(4) > .barContent.chapterList > div:nth-child(2) > table.listing > tbody > tr:nth-of-type(2n+2) {
    background: #1D1D1D;
}
#containerRoot > div:nth-child(11) > div:nth-child(1) > div:nth-child(2) {
    color: #fff!important;
}
.textarea-wrapper {
    background: #171717;
    border: none!important;
    border-radius: 0px;
    color: #fff;
}
.textarea-wrapper .placeholder {
    color: #9C9C9C!important;
    line-height: 30px;
    font-size: 14px;
}
.textarea-wrapper .textarea {
    color: #9C9C9C!important;
    font-size: 14px;
    font-family: "Meiryo";
}
.listing tr.odd {
    background: #1D1D1D;
}
.barContent.episodeList > div:nth-child(2) > div:nth-child(1) {
    color: #eee!important;
}
#footer {
    background: #171717;
}
#subcontent div div.blue {
    background: #171717;
    font: normal 12px Meiryo;
}
#tab-newest > div > div:nth-child(5) {
    display:none;
}
div#tab-mostview > div > div:nth-child(5) {
    display:none;
}
#tab-newest > div > p:nth-child(4) > a {
    color: #B5B5B5;
}
#tab-mostview > div > p:nth-child(4) > a {
    color: #B5B5B5;
}
#subcontent div div {
    background: #171717;
    font: normal 12px Meiryo;
    border-top: 1px solid #111111;
}
div#divFloatLeft, div#divFloatRight {
    display: none!important;
}
form#formSearch > div:nth-child(3) {
    display: none!important;
}
div#___plusone_1 {
    display: none!Important;
}
.barContent > div > .clear2 {
    display: none!important;
}
.kmn-div {
    display: none!important;
}
.mgbox {
    display: none!important;
}
img#btnSubmit {
    -webkit-filter: grayscale(1) brightness(0.6) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.6) contrast(1.5);
    filter: grayscale(1) brightness(0.6) contrast(1.5);
    transition: 0.2s;
}
img#btnSubmit:hover {
    -webkit-filter: grayscale(1) brightness(0.7) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.7) contrast(1.5);
    filter: grayscale(1) brightness(0.7) contrast(1.5);
}
img#btnReset {
    -webkit-filter: grayscale(1) brightness(0.6) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.6) contrast(1.5);
    filter: grayscale(1) brightness(0.6) contrast(1.5);
    transition: 0.2s;
}
img#btnReset:hover {
    -webkit-filter: grayscale(1) brightness(0.7) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.7) contrast(1.5);
    filter: grayscale(1) brightness(0.7) contrast(1.5);
}
img[title="Latest update"], img[title="Latest update series"] {
    -webkit-filter: invert(1) saturate(0.3) hue-rotate(69deg);
    -moz-filter: invert(1) saturate(0.3) hue-rotate(69deg);
    filter: invert(1) saturate(0.3) hue-rotate(69deg);
}
img[title="Most Popular Manga"], img[src="../../Content/images/hot.png"] {
    -webkit-filter: invert(1) hue-rotate(175deg) saturate(0.7);
    -moz-filter: invert(1) hue-rotate(175deg) saturate(0.7);
    filter: invert(1) hue-rotate(175deg) saturate(0.7);
}
input#btnSubmit {
    -webkit-filter: grayscale(1) brightness(0.6) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.6) contrast(1.5);
    filter: grayscale(1) brightness(0.6) contrast(1.5);
    transition: 0.2s;
}
input#btnSubmit:hover {
    -webkit-filter: grayscale(1) brightness(0.7) contrast(1.5);
    -moz-filter: grayscale(1) brightness(0.7) contrast(1.5);
    filter: grayscale(1) brightness(0.7) contrast(1.5);
}
#subcontent > div > div:nth-child(11) {
    font-size: 12px!important;
    text-align: center!important;
    padding-top: 4px;
    padding-bottom: 4px;
}
#rightside > .rightBox:nth-child(5) > .barContent > div:nth-child(2) > div:last-child {
    font-size: 12px!important;
    text-align: center!important;
    border-top: 1px solid #111;
    padding-top: 3px!important;
}
#rightside > .rightBox:nth-child(5) > .barContent {
    padding-bottom: 3px;
}
.alphabet {
    background: #171717!important;
}
#submenu {
    margin-top: -20px;
}
div#divEmailNotify {
    margin-bottom: 10px;
}
#leftside > .bigBarContainer > .barContent.chapterList > div > table.listing > tbody > tr:nth-child(1) {
    font-size: 0;
    height: 0;
}
#leftside > .bigBarContainer:nth-child(4) {
    background: transparent;
    overflow: hidden;
}
#leftside > .bigBarContainer:nth-child(4) > .barContent {
    background: #171717;
    position: relative;
    bottom: 76px;
}
#leftside > .bigBarContainer:nth-child(4) > .chapterList > div > table.listing > tbody > tr > td:nth-child(2) {
    text-align: right;
    padding-right: 5px;
}
#leftside > .bigBarContainer:nth-child(4) > .chapterList > div > table.listing > tbody > tr > td:nth-child(1) {
    padding-left: 5px;
}
td {
    transition: 0.2s;
    padding-left: 5px;
}
.txtSignUp {
    border: none;
    background: #393939;
    font: normal 12px Meiryo;
}
#genres a {
    background: url("http://puu.sh/lLsI5/42bdb261c3.png") no-repeat;
}
p {
    color: #9E9E9E;
}
.info {
    color: #F7F7F7;
}
.textDark {
    color: #B5B5B5;
}
div.barContent > div:nth-child(4) > table.listing:nth-child(11) {
    background: #171717;
}
.barContent > div > p > b {
    color: #9C7EC3;
}
.bigBarContainer > .barContent > div > div.clear {
    display: none;
}
#leftside > .bigBarContainer:nth-child(1) > .barContent:nth-child(2) > div:nth-child(4) > div:nth-child(11) {
    display: none!important;
}
div#divAds2 {
    display: none!important;
}
#leftside > div:nth-child(10) {
    display: none!important;
}
iframe {
    visibility:hidden!important;
    height: 0!important;
}
#footcontainer p {
    color: #404040!important;
}
#footcontainer p a {
    color: #333;
}
.float-ck {
    display: none!important;
}
span.st_fblike_hcount,
span.st_facebook_hcount,
span.st_twitter_hcount,
span.st_googleplus_hcount,
span.st_sharethis_hcount {
    display:none!important;
}
iframe[id="dsq-app1"] {
    visibility: visible!important;
}
a[href="/Register/Member"] {
    display: none;
}
img[id="imgLogo"]{
    -webkit-filter: invert(0.935);
    -moz-filter: invert(0.935);
    filter: invert(0.935);
}
