style.css:
@-moz-document url-prefix("https://habrahabr.ru") {
/* Отключаем баннеры в правом sidebar */
.sidebar_right a[target="_blank"], .company-banner, .xyz_wrapper {
	width: 0 !important;
	height: 0 !important;
	margin: 0 !important;
        overflow: hidden;
}

/* Отключаем Яндекс.Директ, баннеры компании и другие */
.float_yandex_ad, .html_banner, .adbanner, .no_please_one_one_one, .firskill__label, #print_tab, .bmenu_inner, .company_top_banner {
	display: none !important;
	width: 0 !important;
	height: 0 !important;
	overflow: hidden;
}

/* Отключаем вывод social media marketing */
.company_widgets, .facebook_like_box, .vkontakte_groups, .hantim_vacanies {
	display: none !important;
}

/* Отключаем сообщение об использовании adBlock */
.lain_13_what_are_you_doing {
	display: none !important;
}
}
