style.css:
@-moz-document domain('news.yahoo.co.jp'),
domain('headlines.yahoo.co.jp') {

    :root {
        --col-bg-hover-normal: none !important;
        --col-bg-hover-normal-pc: none !important;
    }

    /* リンク、記事リンク */
    p.ttl a, dl.mc dt a, ul.list.topics a, dl.article dt, span.ttl a, dt.title a, li.listFeedWrap a, .listModBox a dl dt, div.newsFeed_item_title, p.topics_item_title {
        color: #000 !important;
        font-weight: normal !important;
    }
    a {
        color: #000 !important;
    }
    dl.article dt {
        font-size: 1.0em !important;
    }
    .newsFeedTab_item-current span {
        color: #a1c3db !important;
    }
    /* 記事タイトル、セクションタイトルの太文字解除 */
    h1, h2,em {
        color: #000 !important;
        background-color: #ccc !important;
        font-weight: normal !important;
    }

    /* 背景色 */
    div.mainBox,
    div.mainBox .module,
    div.mainBody,
    div#yjsmhLogo,
    div#yjsmhPremHeader,
    div#main_cnt_wrapper,
    div#wrapper,
    div#content,
    div.contentsWrap,
    div#contents,
    div#contentsBody,
    section.contents,
    section.cateNews,
    section.categoryWrap,
    section.categoryWrap ,
    div.epCategory,
    div.subCategory,
    div.subCategory ul li,
    header.topHd,
    nav.selector,
    div#topicsList, div#newsFeed,
    header.hd,
    div.photo ul,
    div.btn,
    article.articleDefault,
    div.listRowlink ul li,
    section.cateNews div ul li,
    div.cateTtl,
    div.articleList,
    div.article,
    div.seriesList,
    ul.articleList li,
    ul.cateNavList,
    ul.cateNavList li,
    div.headline,
    div.epCategory .cat,
    div.baseBox,
    div.reseachCol_right,
    div.reseachCol_left,
    div.chartBar li,
    div.im3,
    div#contents.mag,
    div#wrapper,
    div#contentsHeader,
    div#headerSearch,
    div.listTextlink,
    div.listTextlink ul li,
    div.fdFt,
    li.listFeedWrap,
    div.epContents,
    ul.picList,
    article.anchorMod a,
    div.anchorMod a,
    div.relLink,
    div.categoryFtNav,
    nav.listNav,
    nav.contents, nav.contents section article, nav.contents section article ul,
    nav.contents section section ul li,
    div.pagenation,
    div.areaNav,
    header.labelSmall div,
    div.mainBox section div,
    p.toWebBtn a,
    div.hd p,
    div.commentsNav ul li span,
    div.commentsNav ul li,
    footer,
    footer div ul li a,
    footer a,
    div.footerInner, 
    div.buttonMedium p,
    div.newsFeedTab ul.newsFeedTab_list,
    ul.newsFeedTab_list li,
    li.newsFeed_item,
    li.topics_item,
    nav.newsFeedTab, ul.newsFeedTab_list,
    ul#mediaList li,
    header.pageTitle,
    nav#yjsmhMenu,
    media#contents,
    div.mediaArticleListMain,
    div.newsFeed,
    div[class^="pickup"]
    {
        background-color: #ccc !important;
        color: #000 !important;
        --col-bg-lv1: #ccc  !important;
    }

    a.btnView {
        color: #ccc !important;
    }
    li.listFeedWrap,
    ul.listModBox li ,
    li.listModBoxWrap{
        border-bottom: 1px solid #ccc;
    }
    ul.newsFeed_list li.newsFeed_item {
        border-top: 1px solid #ccc;
    }
    div.fdFt .ftPager li a {
        background-color: unset ! important;
    }
    .newsFeed_item-normal .newsFeed_item_text::before {
        height: 0 !important;
    }

    div#msthd,
    div#yjsmhLogo,
    div#yjsmhLogo h1,
    div.gnSecWrap,
    div.yjnHeader_tools,
    div.yjnHeader_nav_sub,
    section.mediaList ul.topNav li a,
    ul.articleNav li a,
    nav#categoryNav,
    nav#categoryNav li a
    {
        background-color: #ccc !important;
        color: #000 !important;
        --col-bg-lv1: #ccc  !important;
    }

    .categoryTab_list,
    .categoryTab_item,
    .yjnHeader_nav_main li
    {
        background-color: #bbb !important;
        color: #000 !important;
        background-image: none !important;
    }
    .categoryTab_item-current, 
    .yjnHeader_nav_main li.current
    {
        background-color: #999 !important;
    }

    form.searchBox,
    div.headerSearch,
    div.searchWrapper,
    div.inputWrapper,
    aside.searchMd,
    form div.frame
    {
        background-color: #ccc !important;
        color: #000 !important;
        --col-bg-lv1: #ccc  !important;
    }
    input.search,
    input.searchInput,
    input#searchText,
    li.yjnHeader_serch_input input,
    select
    {
        background-color: #ccc !important;
        color: #000 !important;
    }

}
