style.css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com") {
body.fbIndex {
    background: url('http://phandroid.s3.amazonaws.com/wp-content/uploads/2014/06/forest-patrol.jpg')left center fixed !important;
    -webkit-background-size: cover;
    -moz-background-size: 100% !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
    overflow-y: hidden
}
.loggedout_menubar_container {
    background: none !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
    display: none !important
}
.fbIndex .loggedout_menubar_container {
    position: fixed !important;
    width: 420px !important;
    height: 82px !important;
    min-width: 0 !important;
    top: 35% !important;
    left: 35% !important;
    margin-top: -17px !important;
    margin-left: auto !important;
    z-index: -1 !important;
}
.fbIndex .loggedout_menubar {
    width: 500px !important;
    height: 170px;
   	background-color:rgba(0, 0, 0, 0.5)!important; 
    padding-top: 20px;
    padding-left: 30px;
    box-shadow: 0px 10px 30px 0px #000;
    transition: all .3s ease-out;
}

.fbIndex .loggedout_menubar:hover {
   	background-color:rgba(0, 0, 0, 0.7)!important; 
    box-shadow: 0px 10px 30px 0px #000;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
    float: none !important
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
    display: block !important;
    margin: 0 auto !important
}
.fbIndex .loggedout_menubar_container .lfloat img {
    display: block;
    margin: -60px auto 20px !important;
}
._5a-- {
    display: none !important
}
#SetAsHomepage_Callout {
    display: none;
}

/*INPUT*/
.inputtext, .inputpassword {
    background-color: transparent;
    height:25px;
    border-top-color: transparent!important;
    border-right-color: transparent!important;
    border-left-color: transparent!important;
    border-bottom-color: #fff!important;
	transition: all .3s ease-out;
    font-family: roboto, tahoma, arial, sans-serif!important;
    color: #fff;
    font-weight: normal;
}


.inputtext:hover {
    outline: none;
}

.inputtext:focus {
    outline: none;
}


/*SUBMIT*/

.uiButtonConfirm {
    background-image: none!important;
    background-color: transparent;
    border-top-color: transparent!important;
    border-right-color: transparent!important;
    border-left-color: transparent!important;
    border-bottom-color: #fff!important;
    width: 82px;
    height: 28px;
    transition: all .3s ease-out;
}

.uiButtonConfirm:hover {
    background-color: transparent;
}

.uiButtonConfirm:focus {
    background-image: none!important;
}


.uiButton:focus, .uiButtonSuppressed:active, .uiButtonSuppressed:focus, .uiButtonSuppressed:hover {
    background-image:none!important;
}

.uiButtonText, .uiButton 
input {
    border: 0;
    color: #fff;
    cursor: normal;
    display: inline-block;
    font-size: 11px;
    font-weight: normal;
    line-height: 35px;
    transition: all .3s ease-out;
    font-family: roboto, tahoma, arial, sans-serif!important
}

.uiButtonText, .uiButton 
input:hover {
    color: #888;
    
}

.uiButtonText, .uiButton 
input:focus {
    background-image: none;
    background-color: transparent!important;
    outline: none;
}
/*LOGO*/
