}

/*LABEL'S*/
.menu_login_container .html7magic label {
    color: #fff;
    font-weight: normal;
    font-family: roboto, tahoma, arial, sans-serif!important
}

/*OPCIONES ABAJO*/
.menu_login_container .login_form_label_field label, .menu_login_container .login_form_label_field a {
    color: #FFFFFF;
    font-weight: normal;
    font-family: roboto, tahoma, arial, sans-serif!important
}
















