box-shadow: inset 0px 0px 10px 3px rgba(/*[[boxshadowcol-rgb]]*/,/*[[boxshadowtrans]]*/
) !important;
transition: none !important; 
}

/*hover off*/
#content-section.ytd-channel-renderer /*channel search results */
,ytd-playlist-renderer /*play list results*/

{
