style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.wanikani.com"), url-prefix("http://wanikani.com/"), url-prefix("https://wanikani.com/") {

  
/*-----------------------------------------------------------------------------*/
/*------------------------------- FORUM ---------------------------------------*/
/*-----------------------------------------------------------------------------*/

section.dashboard-sub-section > h3.small-caps {
  text-align: left !important;
  padding: 0 30px !important;
  margin: 0 !important;
}
  
.heading-symbol {
  border: 0px !important;
  box-shadow: none !important;
  margin: 0 !important;
  display: inline !important;
  height: auto !important;
  width: auto !important;
}
  
.DATP-i-small{
  left:100% !important;
}

/*-----------------------------------------------------------------------------*/
/*------------------------------- SRS PROGRESS --------------------------------*/
/*-----------------------------------------------------------------------------*/
  
.dashboard section.srs-progress ul li {
  padding: 15px !important;
  font-size: 0px !important;
  box-shadow: none !important;
  display: inline-flex !important;
  justify-content: center !important;
  align-items: center !important;
} 
  
.dashboard section.srs-progress ul li span {
  font-size: 30px !important;
  margin: 0px !important; 
}  
  
.dashboard section.srs-progress ul li:first-child {
  border-radius: 5px 0px 0px 5px !important;
}
  
.dashboard section.srs-progress ul li:last-child {
  border-radius: 0px 5px 5px 0px !important;
}
  
/*-----------------------------------------------------------------------------*/
/*------------------------------- REVIEW STATUS --------------------------------*/
/*-----------------------------------------------------------------------------*/
  .dashboard section.review-status ul li {
    padding: 10px !important;
  }
  
  .dashboard section.review-status {
    display: none !important;
  }
  
  
/*-----------------------------------------------------------------------------*/
/*------------------------------- PROGRESS STATUS ------------------------------*/
/*-----------------------------------------------------------------------------*/
  .dashboard .lattice-single-character {
    /*[[dropdown]]*/
  }
  
  .progression h3 {
    font-size: 1em !important;
    margin: 0 0 10px 0 !important;
  }
  
/*-----------------------------------------------------------------------------*/
/*------------------------------- USELESS PANELS ------------------------------*/
/*-----------------------------------------------------------------------------*/
  
  .recent-unlocks {
    /*[[uselessPanes]]*/
  }
  
  .low-percentage {
    /*[[uselessPanes]]*/
  }
  
  .recent-retired {
    /*[[uselessPanes]]*/
  }
  
}
