style.css:
@-moz-document url-prefix("http://ficbook.net/readfic/"), url-prefix("https://ficbook.net/readfic/") {
:root
{
    --bg_img: url("data:image/png;base64,")
}
.fanfic-main-info{text-align:center !important}
/*a.btn-with-description, a.btn-with-description:hover, a.btn-with-description:visited, a.btn-with-description:visited:hover 
{
    color:white !important
}*/
/*REMOVE VK-FB-TW icons + ads*/
/*.rkl-block{display:none!important}*/
.visible-lg.display-none    /*.py-15.px-5*/
{
    display: none!important
}
.col-sm-2 ~ .col-sm-2   /*Social icons*/
{
    display: none
}
.hat-footer-row > .col-sm-2:first-of-type
{
    width: 25%
}
/*Restore marks column width*/
/*.in-text-ad-left, .part-text-ad, #yandex_rtb_2 {display:none} 	/*Remove ADS*/
BODY/*, .ui-widget, .ui-widget-content*/
{
    color: /*[[var_textcolor]]*/ !important;
    /*осн. цвет текста*/
    /*font-family: Blogger Sans;*/
    /*тут можно выбрать шрифт текста*/
    background: #050505 var(--bg_img) repeat fixed !important;
}
/*фоновая картинка*/
.tag-category
{
    color: /*[[var_textcolor]]*/ !important
}
/*цвет тегов*/
/*img.normal {color: crimson !important;}							/*перекрасить SVG при помощи CSS*/
/*.info {color: #88f!important;border-color: #88f!important; box-shadow: 0 0 4px #aaf, inset 0 0 4px #aaf; border-radius: 10px; margin-left: 6%;}*/
/*#main > div:first-child {display: none !important;}*/
a:not(.btn), .info strong, .btn-link, .as-link, /*цвет ссылок, предупреждений и т.п.*/
strong.js-show-hidden-tags,.start-reading  /*spoilers*/
{
    color: #fca!important;
}
a.btn-block
{
    color: #76482e!important
}
/*,not([data-toggle="tooltip"]):*/
a:not(.btn):visited
{
    color: #acf !important
}
/*цвет посещ.ссылок*/
a.notice-yellow
{
    color: black !important
}
/*цвет ссылки ждет критики*/
a:not(.btn):hover, a:not(.btn):visited:hover, .btn-link:hover
{
    color: #f44!important
}
/*цвет ссылок при наведении курсора*/
.js-span-link
{
    color: #4f4!important;
    transition: color 0.5s ease !important;
    border: none!important
}
/*"... и ещё Х наград"*/
.js-span-link:hover
{
    color: #f44!important;
    transition: color 0.2s ease !important
}
/*.public-beta-badge, .badge-green, sup.count цвет текста статуса ПБ и оценок
{
    color: #4f4!important
}*/
.comment-like.liked    /*цвет лайка комментария*/
{
    color: #4f4!important;
    transition: color 0.2s!important;
}
.comment-like.liked:hover
{
    color: #ff8!important;
    transition: color 0.2s !important;
}
.navbar a
{
    transition: color 0.2s ease !important;
    text-shadow: 0 0 4px black;
}
.navbar a:hover, .navbar a:visited:hover
{
    color: #105 !important;
    transition: color 0.2s ease !important;
    text-shadow: 0 0 8px black;
}
.premium-notice > span
{
    width: 100%!important;
    left: 0!important;
    border-radius: 5px;
}
/*плашки перевода и т.п.*/
.premium-notice > span::before
{
    display: none !important
}
.comment-content, #loginErrorMsg, .btn, .form-search
{
    border: none!important
}
/*убрать рамки и подчёркивания (поиск, комменты, кнопки)*/
.navigation-button, .start-reading  /*обновление интерфейса 7/2020 - кнопки навигации*/
{
    background-color: rgba(246, 236, 218, 0.15)!important
}
.navigation-button:hover,.start-reading:hover    /*обновление интерфейса 7/2020 - кнопки навигации*/
{
    background-color: rgba(246, 236, 218, 0.2)!important;
    box-shadow: 0 0 4px 1px rgba(246, 236, 218, 0.25)
}
.btn
{
    outline: 0!important;
    transition: all 0.2s ease !important
}
/*все кнопки*/
.btn:hover
{
    outline: 0!important;
    transition: all 0.2s ease !important
}
.pairing-highlight, ul.list-of-fanfic-parts > li    /*фон тегов пейринга и списка глав*/
{
    background-color: rgba(255, 255, 255, 0.1)!important;
    border-radius: 2px;
}
ul.list-of-fanfic-parts > li /*список глав*/
{
    margin-bottom:3px!important;
    padding:0 10px !important;
}
/*вкладки других работ автора и похожих работ*/
.slide > .list-inline a
{
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    color: #76482e!important;
}
.slide > .list-inline a:hover
{
    background-color: #89734e !important;
    color: #fff!important
}
/*ссылки на вкладки похожих работ*/
.slide > .list-inline a:hover:active
{
    background-color: #726041 !important
}
.book-container, .book-container > div, #main
{
    background: none!important;
}
/*убрать картинки фона*/
.header-b, .navbar
{
    border-radius: 20px!important;
}
.jsScrollToComment, .comment_message
{
    line-height: 150%!important
}
/*высота строки в комментах и форме комментирования*/
.jsScrollToComment, textarea#pbId
{
    color: #efa!important;
    background-color: rgba(255, 255, 255, 0.125)!important
}
/*цвет текста/фона формы*/
h2
{
    font-size: 1.25em !important;
}
/*глава*/
time, .part-date
{
    color: #f82!important;
}
/*время главы и комментов*/
/*.w1, .main-holder, .form-search, #footer*/
.header-holder, .navbar
{
    background: none !important
}
/*убрать цвет фона хедера*/
.header-b /*цвет фона хедера*/
{
    background-color: rgba(255, 255, 255, 0.2)!important;
}
article.block
{
    background-color: rgba(215, 255, 215, 0.1)!important;
    border: none!important;
}
/*элем. списка похожих фиков*/
article.fanfic-block-read
{
    background-color: rgba(255, 240, 172, 0.1)!important;
}
/*то же, но прочитанных*/
.fanfic-thumb-block, .block .description .info, .block .description .info dt
{
    color: inherit!important
}
/*фикс цвета текста в похожих фиках*/
/*.read-decoration-text {background:rgba(255,255,128,0.1)!important; border-color:#662!important; border-bottom-left-radius:15px;border-bottom-right-radius:15px;margin: 4px 0 -4px 0}*/
/*.btn-group>.btn{background-color: rgb(64, 64, 80); color:#8af; border:none;}*/
/*.form-comments .btn-default*/
.form-comments .btn-default
{
    background-color: rgba(96, 255, 96, 0.5);
    color: #fed;
    box-shadow: 0 0 4px 1px rgba(0, 255, 0, 0.3)
}
/*кнопка отправить (коммент)*/
.form-comments .btn-default:hover
{
    background-color: rgba(80, 255, 80, .7);
    color: #fed;
    box-shadow: 0 0 8px 2px rgba(0, 255, 0, .5)
    /*box-shadow: 0 0 8px 2px rgba(255, 255, 255, .5)*/
}
.quoted /*цвет текста и фона цитат коммент*/
{
    color: #efa!important;
    background-color: rgba(255, 255, 255, 0.2)!important;
}
.fanfic-hat-body, .fanfic-reward-container, .comment-content, .give_comment_reward_div
/*фон шапки, комментов и "наградить коммент"*/
{
    background-color: rgba(255, 255, 255, .1)!important;
    box-shadow: 0 0 2px rgba(255, 255, 255, 0.2)!important;
}
/*.premium-notice>span::before,*/
.comment-content::after, .comment-content::before
{
    display: none !important
}
/*.collection_list_link_added {background: none; color: #8F8; text-shadow: 0 0 3px #8F8;}*/
.cross::before, .cross::after
{
    border-bottom-color: #f44!important
}
.turned-off > span
{
    color: #ff4!important
}
/*.turned-off {opacity: 1!important;}*/
.close
{
    padding-bottom: 4px;
    color: red!important;
    border: none!important;
    text-shadow: 0 0 4px #000, 0 0 2px #000 !important;
    background-color: rgba(255, 255, 255, 0.5)!important;
    border-radius: 50%;
    width: 32px;
    height: 32px;
}
/*значок креста закрытия*/
/*.alert-info {background-color: #235!important; color: #d9edf7!important; text-align: center; border: none; box-shadow: 0 0 8px 2px #235; border-radius: 10px;}*/
@media (max-width: 1366px)
{
    #toTop
    {
        right: 40px!important;
    }
}
/*кнопка "наверх"*/
/*.check-holder {margin-left:-16px!important; color:black;}*/
button[id="chooseBgColor"], .book-inner > div[class^="pages"]
{
    display: none !important
}
/*кнопка цвета фона, визуал "страниц"*/
