.checkbox input
{
    position: relative !important;
}
.form-control[name="name"]
{
    margin-left: 10px;
    background-color: rgba(255, 255, 255, 0.15);
    color: #efa!important;
    border-color: rgba(255, 255, 255, 0.505);
    border: none
}
.logo
{
    filter: invert(.8)
}
.search-input
{
    color: black;
}
a.tag:not(.disliked-parameter-link):not(.liked-parameter-link)
{
    background: rgba(234, 226, 209, 0.15)!important
}
.col-sm-4 svg
{
    color: unset !important
}
.fanfic-download-description
{
    color: initial
}
