style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com") {
    .lj-like {
        display:none;
    }
    .l-flatslide-container {
        max-width: 1000px;
    }
    .l-flatslide-aside, .b-feedwidgets {
        display: none;
    }

    .l-flatslide-content {
        margin: 0 50px 4em 50px;
    }

    .b-lenta.b-feedsettings-on .l-flatslide-control {
        opacity: 0.2;
    }

    .l-flatslide-navigation {
        display:none !important;
    }

    header.s-header,
    .appwidget-stylealwaysmine, .s-header, .s-header-in, .s-header-journal, .s-header-journal__user, .s-logo, .w-cs-view-style,
    .s-footer {
        background-color: #003040 !important;
    }

    .b-lenta .b-sticky-cut {
        margin-left: -410px;
    }

}
