    transform: translate3d(/*[[__FOLDER_WIDTH]]*/, 0px, 0px);
    visibility: visible;
    background: -webkit-linear-gradient(top, rgba(255,255,255,0.75) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.75) 100%);
    background: linear-gradient(to bottom, rgba(255,255,255,0.75) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.75) 100%);
    background-color: #E1E1E1;
    border-top: 1px inset #A1A1A1;
    border-right: 1px outset #A1A1A1;
}
/* folder list popup in the preview panel */
.folder-tray-float {
    background: -webkit-linear-gradient(top, rgba(255,255,255,0.75) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.75) 100%);
    background: linear-gradient(to bottom, rgba(255,255,255,0.75) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.75) 100%);
    background-color: #E1E1E1;
    top: 30px;
    color: #325c80;
    box-shadow: 4px 4px 4px 0px #325c80; /*__MEDIUM_3;*/
}
/* folder view and spacing of folder names */
.folder-view .this-folder {
    padding-left: 10px;
    height: 16px;
}
.folder-view .this-folder,
.folder-view .folder-name {
    font-size: 13px;
    line-height: 20px;
    font-weight: normal;
}
.folder-view .this-folder:hover {
    padding-left: 7px;
    font-style: oblique;
    font-weight: bold;
    border-left: 3px solid magenta;
    color: /*[[__RED]]*/;
}
.folder-view.loadedFolderSelect > .this-folder:before {
    height: 16px;
    border-right: 3px solid #4178be;
}
.folder-view .this-folder .folder-icon,
.folder-view .this-folder > .this-folder-container .folder-icon {
    fill: #4178be;
}
.folder-tray .folder-label {
    margin-top: -14px;
    margin-left: -9px;
}
.folder-tray-float .folder-label {
    margin-left: -9px;
}
.folder-tray .folder-search-input,
.folder-tray-float .folder-search-input {
    margin-left: 12px;
}
.seq-mcv .facets-container .facets-title .refine {
    margin-top: -2px;
}
/* Left and right main panels ---------------------------------------------- */
/* the compose window */
.dockable.docked-compose {
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
/* the inbox */
.seq-mcv .inbox-container {
    top: 30px;
    padding-top: 0px;
    height: calc(100% - 32px);
    border-right: 1px outset #A1A1A1;
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
/* inbox placement overrides */
/* the inbox listing when no folders shown */
.seq-mcv.preview .inbox-container,
.seq-mcv.batch-edit-mode .inbox-container {
    width: /*[[__INBOX_WIDTH]]*/;
    background-color: #F8F8F8;
}
/* the inbox listing when folders/facets shown */
.seq-mcv.preview.facets-open .inbox-container,
.seq-mcv.preview.folders-open .inbox-container,
.seq-mcv.batch-edit-mode.facets-open .inbox-container,
.seq-mcv.batch-edit-mode.folders-open .inbox-container {
    left: /*[[__FOLDER_WIDTH]]*/;
    width: /*[[__INBOX_WIDTH]]*/;
    background-color: #F8F8F8;
}
/* the refresh button */
.seq-mcv.preview .left-header,
.seq-mcv.batch-edit-mode .left-header {
    width: /*[[__INBOX_WIDTH]]*/;
    height: 30px !important;
    margin-top: 0px;
    background-color: #F8F8F8;
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
/* the mail text is in the preview window */
.preview-panel {
    left: /*[[__INBOX_WIDTH]]*/;
    width: calc(100% - /*[[__INBOX_WIDTH]]*/);
    -webkit-transition: opacity 333ms ease 0s;
    transition: opacity 333ms ease 0s;
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
/* this resets the preview to consume all space in the torn off menu */
.torn-off-read {
    top: 0% !important;
    left: 0% !important;
    width: 100% !important;
    height: 100% !important;
}
/* the preview panel when folders open */
.seq-mcv.folders-open.preview .preview-panel,
.seq-mcv.facets-open.preview .preview-panel {
    left: calc(/*[[__INBOX_WIDTH]]*/ + /*[[__FOLDER_WIDTH]]*/);
    width: calc((100% - /*[[__INBOX_WIDTH]]*/) - /*[[__FOLDER_WIDTH]]*/);
}
/* when there's nothing in the preview, folders shown */
.seq-mcv.folders-open.preview .preview-panel.empty,
.seq-mcv.facets-open.preview .preview-panel.empty {
    left: calc(/*[[__INBOX_WIDTH]]*/ + /*[[__FOLDER_WIDTH]]*/);
    width: calc((100% - /*[[__INBOX_WIDTH]]*/) - /*[[__FOLDER_WIDTH]]*/);
}
/* when there's nothing in the preview, folders not shown */
.preview-panel.empty {
    left: /*[[__INBOX_WIDTH]]*/;
    width: calc(100% - /*[[__INBOX_WIDTH]]*/);
    background-color: white;
    opacity: 0.075;
}
/* empty ain't empty */
.preview-panel.empty .content {
    background-color: white;
    opacity: 1.0;
    transition: opacity 3s ease 2s;
}
.preview-panel.empty .content:hover {
    opacity: 0;
    background-repeat: no-repeat;
    background-position: bottom right;
/* this is the dark version */
    background-image: url('data:image/png;base64,');
}
/* batch mode is when you have something selected via the avatar ------------ */
/* when the folders are not shown */
.seq-mcv.batch-edit-mode .message-batch.message-batch-panel {
    left: /*[[__INBOX_WIDTH]]*/;
    width: calc(100% - /*[[__INBOX_WIDTH]]*/);
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
/* when the folders are shown */
.seq-mcv.batch-edit-mode.facets-open .message-batch.message-batch-panel,
.seq-mcv.batch-edit-mode.folders-open .message-batch.message-batch-panel {
    left: calc(/*[[__INBOX_WIDTH]]*/ + /*[[__FOLDER_WIDTH]]*/);
    width: calc((100% - /*[[__INBOX_WIDTH]]*/) - /*[[__FOLDER_WIDTH]]*/);
    opacity: /*[[__IMAGE_OPACITY]]*/;
}
.seq-mcv.batch-edit-mode .message-batch.message-batch-panel .content {
    background: -webkit-linear-gradient(top, rgba(255,255,255,1) 0%,rgba(255,255,255,0) 100%);
    background: linear-gradient(to bottom, rgba(255,255,255,1) 0%,rgba(255,255,255,0) 100%);
    background-color: #F8F8F8;
}
.seq-mcv.batch-edit-mode .message-batch.message-batch-panel .action-bar,
.seq-mcv.batch-edit-mode .message-batch.message-batch-panel .content .batch-message-container .seq-msg-row,
.seq-mcv.batch-edit-mode .message-batch.message-batch-panel .content .batch-message-container .seq-msg-row.selected:not(:focus) {
    background-color: #F8F8F8;
}
/* the folder and facets toggles, the little buttons above the inbox */
.seq-mcv .left-header {
    border-bottom: 1px outset #A1A1A1;
    border-right: 1px outset #A1A1A1;
}
.seq-mcv .left-header button svg {
    background-color: #F8F8F8;
}
.seq-mcv .filters .toggle {
    border-bottom-color: #F8F8F8;
}
.seq-mcv.facets-open .facets-toggler:hover svg:hover,
.seq-mcv.folders-open .folders-toggler:hover svg:hover {
    fill: /*[[__RED]]*/;
}
.seq-mcv.facets-open .facets-toggler svg,
.seq-mcv.folders-open .folders-toggler svg,
.seq-mcv .facets-toggler svg,
.seq-mcv .folders-toggler svg,
.seq-mcv .facets-toggler,
.seq-mcv .folders-toggler {
    fill: #325c80;
    color: #325c80;
    border-bottom-color: #F8F8F8;
    background-color: #F8F8F8;
}
/* Set up of the link and files button position when the folder tray is open */
.seq-mcv.folders-open .facets-toggler,
.seq-mcv.facets-open .facets-toggler {
    margin-right: 100px;
}
.seq-mcv .filters,
.seq-mcv.facets-open .filters,
.seq-mcv.folders-open .filters {
    margin-top: 0px;
    border-left: 1px inset #A1A1A1;
}
.seq-mcv .message-list-buttons {
    margin-top: 0px;
}
/* scoot up the underlines beneath the words */
.seq-mcv.facets-open .facets-toggler::after,
.seq-mcv.folders-open .folders-toggler::after,
.seq-mcv.links .filters .links.toggle::after,
.seq-mcv.files .filters .files.toggle::after {
    top: 20px;
    border-bottom: 1px solid #C1C1C1;
}
.read-view.comp-actions-reply .reply:after,
.read-view.comp-actions-reply-all .reply-all:after,
.read-view.comp-actions-forward .forward:after {
    top: 18px;
    border-bottom: 1px solid #C1C1C1;
}
/* the buttons for folder/preview */
.seq-mcv.preview.facets-open .left-header,
.seq-mcv.preview.folders-open .left-header,
.seq-mcv.batch-edit-mode.facets-open .left-header,
.seq-mcv.batch-edit-mode.folders-open .left-header {
    width: calc(/*[[__INBOX_WIDTH]]*/ + /*[[__FOLDER_WIDTH]]*/);
    background-color: #F8F8F8;
}
/* this will cause a buzzzzzzz :-)  -------------------------------------- */
.seq-mcv .bee:hover {
    opacity: 0.99;
    fill: #325c80;
    height: 500px;
    -webkit-transform: rotate(720deg);
    transform: rotate(720deg);
}
.seq-mcv .bee {
    fill: #D1D1D1;
    opacity: 0.01;
    -webkit-transition: fill 2s, width 2s, height 2s, opacity 2s, -webkit-transform 2s;
    transition: fill 2s, width 2s, height 2s, opacity 2s, transform 2s;
}
/* ohhhh, ahhhh, what's this??  ------------------------------------------------- */
.experiments-pane {
    display: none; /* change to block for some interesting items...  */
}
}
/* That's all Folks!! ----------------------------------------------------------- */

