@-moz-document domain("soundcloud.com") {
/* General */

body {
	background-color: #FFF;
	padding-bottom: 75px;
	font-size: 13px;
}

.profileHeader__info {
	width: 900px;
}

.profileHeaderBackground__visual {
	background-size: cover;
	background-position: center;
}

#content,
.l-container.l-fullwidth {
	width: 97%;
}

body {
	overflow-x: hidden;
}

.hidden-thing {
	position: absolute;
	left: 100%;
	width: 50px;
	height: 50px;
	opacity: 100;
}

.dropbar.m-fullWidth {
	width: 100%;
}
}

@-moz-document domain("soundcloud.com") {
/* Controls */

.sc-artwork {
	border-radius: 4px;
}

.image__rounded {
    border-radius: 50%;
}

.g-dark textarea,
.g-dark select,
.g-dark input[type="text"],
.g-dark input[type="password"],
.g-dark input[type="search"] {
	color: #999;
	background-color: #282F33;
}

.g-type-shrinkwrap-inline,
.g-type-shrinkwrap-inline:hover,
.g-type-shrinkwrap-inline:visited,
.g-type-shrinkwrap-block,
.g-type-shrinkwrap-block:hover,
.g-type-shrinkwrap-block:visited {
	position: relative;
	background-color: #282F33;
	border-radius: 4px;
	-webkit-box-decoration-break: clone;
	box-decoration-break: clone;
}

.backgroundGradient {
	opacity: 0;
}

.soundTitle__playButtonHero {
	margin-right: 20px;
}

.sc-button-play {
	border: none;
	background-color: #282F33 !important;
	transition: all 200ms ease;
	transform: scale(1, 1);
}

.sc-button-play:hover {
	border: none;
	background-color: #282F33 !important;
	transform: scale(1, 1);
}

.sc-button-play:focus {
	border: none;
	background-color: #282F33 !important;
	transform: scale(1, 1);
}

.heroPlayButton {
	border: none;
	background-color: #282F33 !important;
	transition: all 200ms ease;
	transform: scale(0.9, 0.9);
}

.heroPlayButton:hover {
	border: none;
	background-color: #282F33 !important;
	transform: scale(1, 1);
}

.heroPlayButton:focus {
	border: none;
	background-color: #282F33 !important;
	transform: scale(0.9, 0.9);
}

.heroPlayButton:focus:hover {
	border: none;
	background-color: #282F33 !important;
	transform: scale(1, 1);
}

.fullListenHero {
	background-color: #282F33 !important;
	height: 420px;
}

.fullListenHero__info {
	right: 260px;
}

.fullListenHero__waveform {
	right: 30px;
}

.fullListenHero__artwork {
	width: 180px;
	height: 180px;
}

.fullListenHero__waveform {
	height: 120px;
}

.commentForm {
	background-color: #EAEFF2;
	border: none;
}

.sc-button-medium,
.sc-button-large,
.sc-button-small {
	background-color: #EAEFF2;
	border: 1px solid #EAEFF2;
	transition: 200ms ease all;
}

.sc-button-medium:hover,
.sc-button-large:hover,
.sc-button-small:focus,
.sc-button-medium:focus,
.sc-button-large:focus,
.sc-button-small:active,
.sc-button-medium:active,
.sc-button-large:active,
.sc-button-small:visited,
.sc-button-medium:visited,
.sc-button-large:visited,
.sc-button-small:visited {
	background-color: #EAEFF2;
	border: 1px solid #f50;
	transition: 200ms ease all;
}

.sc-button-cta,
.sc-button-cta:hover,
.sc-button-cta:focus,
.sc-button-cta:visited {
	background-color: #282F33;
	border: 1px solid #282F33;
	transition: 200ms ease all;
}

.web-profiles {
	font-size: 13px;
}

.sc-ministats-small {
	font-size: 13px;
}

.sc-ministats-followers,
.sc-ministats-sounds {
	font-size: 11px;
}

.sc-status-icon-small,
.sc-status-icon-medium {
	width: 9px;
	height: 9px;
	background-size: 9px 9px;
}

.modal__modal {
	overflow: hidden;
	border-radius: 4px !important;
	padding: 35px;
    -webkit-box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.25);
	-moz-box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.25);
	box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.25);
}

.modalWhiteout {
    background-color: rgba(40, 47, 51, 0.95) !important;
}
}

@-moz-document domain("soundcloud.com") {
/* Navigation bar */

.header__navMenu>li>a {
	border: none;
}

.header {
	background: #282F33 !important;
}

.header__logo {
	background: none;
	transition: 200ms all ease;
	margin-right: 16px;
}

.header__navMenu>li>a.selected,
.header>li>a:focus {
	margin-right: 16px;
}

.header__logo:hover {
	background: #f50;
}

.header__left {
	margin-right: 24px;
}

.header__logoLink:focus {
	background-color: rgba(255, 72, 0, .0);
	outline: 0;
}

.header__link {
	padding: 12px 24px 12px 24px;
	float: left;
}

.header__userNav {
	margin-right: 16px;
	margin-left: 16px;
}

.userNav__button {
	margin-right: 8px;
}
}

@-moz-document domain("soundcloud.com") {
/* Transitions */

.g-all-transitions-500 {
	transition: all 300ms ease;
}

.g-opacity-transition-500 {
	transition: all 300ms ease;
}
/*.visualSound.playing:not(.focused) .sound__waveform {
    opacity: 0;
	margin-top: 50px;
	margin-bottom: 0;
}*/
}

@-moz-document domain("soundcloud.com") {
/* Sounds */

.userStreamItem {
	margin-top: 24px;
	margin-bottom: 24px;
}

.sound__waveform {
	height: 80px;
}

.sound__coverArt {
	width: 180px;
	height: 180px;
	margin-right: 20px;
}

.visualSound.listenContext .visualSound__wrapper {
	padding: 455px 30px 0 30px;
}

.fullListenHero__playerArea {
    right: 30px;
}

.visuals.listenContext {
	height: 600px;
}

.listenContext>.sound__body>.visualSound__wrapper>.visualSound__progress {
	top: 597px;
}

.visualSound.listenContext .visualSound__wrapper {
	padding: 455px 30px 0 30px;
}

.visuals.streamContext {
	height: 180px;
}

.visualSound:not(.listenContext) .sound__footer {
	position: relative;
	bottom: -10px;
	left: 10px;
	right: 10px;
}

.visualSound.streamContext .visualSound__wrapper {
	margin-bottom: 0;
}

.visuals:not(.listenContext) {
	border-radius: 4px;
	overflow: hidden;
}
}

@-moz-document domain("soundcloud.com") {
/* Profiles */

.profileHeaderBackground.m-visualLoaded .profileHeaderBackground__visual {
	opacity: 0.1;
	-webkit-filter: blur(12px);
	-moz-filter: blur(12px);
	filter: blur(12px);
}

.profileHeader {
	height: 480px;
}

.profileHeader__info {
	position: absolute;
	z-index: 1;
	bottom: 0px;
	bottom: 40px;
	left: 30px;
}

.profileHeaderInfo__content {
	bottom: 0px;
	left: 240px;
	width: 100%;
	position: absolute;
}

.profileHeaderInfo__userName {
	font-size: 32px;
	line-height: 37px;
	padding: 9px 9px;
}

.profileHeaderBackground {
	background: #282F33;
}

.featuredProfiles {
	background-color: #282F33;
	border-radius: 4px;
	height: 480px !important;
	-webkit-transform: translateY(440px);
	transform: translateY(440px);
	transition: transform .5s ease;
}

.featuredProfiles__wrapper {
	height: 480px !important;
}
}

@-moz-document domain("soundcloud.com") {
/* Layout Fixes */

.conversation__actions {
	width: calc( 100% - 405px);
}

.l-fixed-top-one-column>.l-top {
	width: 100%;
}

.header__navMenu>li>a.selected, .header>li>a:focus {
    margin-right: 0;
}
}

@-moz-document domain("soundcloud.com") {
/* Dropbars */

.dropbar__content {
	background-color: #282F33;
    color: #fff;
    padding-bottom: 12px;
}

.dropbar {
    border: none !important;
    -webkit-box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.3);
	-moz-box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.3);
	box-shadow: 0px 8px 25px 2px rgba(0,0,0,0.3);
}

.dropbar__content > .sc-border-light-bottom {
    border: none;
}

.userDropbar__buttons > .sc-button-group > .sc-button {
    background-color: #282F33 !important;
    color: #ccc !important;
    border-color: rgba(0, 0, 0, 0.2);
}

.userDropbar__buttons > .sc-button-group > .sc-button:hover {
    border-color: #f50;
}

.userDropbar__buttons > .sc-button-group > .sc-button-medium.sc-button-edit:before {
    display: none;
}

.userDropbar__buttons > .sc-button-group > .sc-button-medium.sc-button-edit {
    text-indent: 0;
}

.userDropbar__buttons > .sc-button-group > .sc-button-icon {
    background-color: #fff !important;
    color: #333 !important;
    border-color: #162026;
}
}

