style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mkvcage.com") {
body {
	color:#fff;
	background:#111;
}

code,pre {
	padding:0 3px 2px;
	font-family:Monaco,Menlo,Consolas,"Courier New",monospace;
	font-size:12px;
	color:#fff;
	-webkit-border-radius:3px;
	-moz-border-radius:3px;
	border-radius:3px
}	
	
pre {
	display:block;
	padding:9.5px;
	margin:0 0 10px;
	font-size:13px;
	line-height:20px;
	word-break:break-all;
	word-wrap:break-word;
	white-space:pre;
	white-space:pre-wrap;
	background-color:#444;
	border:1px solid #ccc;
	border:1px solid rgba(0,0,0,0.15);
	-webkit-border-radius:4px;
	-moz-border-radius:4px;
	border-radius:4px
}	
	
h1,h2,h4,h5,h6 {
	color:#0B6791;
	font-family:"Yanone Kaffeesatz",Arial,Helvetica,sans-serif;
}	
	
h3 {
	color:#fff;
	font-family:"Yanone Kaffeesatz",Arial,Helvetica,sans-serif;
}		
	
button,a.button,input[type="button"],input[type="reset"],input[type="submit"] {
	color:#fff;
	line-height:24px;
	background:#1478AA;
	border:1px solid #060608;
}
button:hover,a.button:hover,input[type="button"]:hover,input[type="reset"]:hover,input[type="submit"]:hover {
	background:#0B6791;
	border-color:#0B6791;
}
	
#main-navigation .sf-menu {
	background: #1478AA;
	}
	
#main-navigation li:hover li a:hover,#main-navigation li.sfHover li a:hover {
	background:#111;
}	
	
.site-header {
	background:#1478AA;
}	
	

.imdb-link-icon a{
    padding-left: 46px !important;
    background-image: url(/*[[IMDB]]*/) !important;
    background-position: 18px center!important;
    background-repeat: no-repeat !important;}
.imdb-link-icon a:hover {
    padding-left: 46px !important;
    background-image: url(/*[[IMDB]]*/) !important;
    background-position: 18px center!important;
    background-repeat: no-repeat !important;}	


.site-content {
	border-right:1px solid #222;
	border-left:1px solid #222;
}

.entry-title a {
	color:#0B6791;
}
.entry-title a:hover {
	color:#fff;
}	
	
.entry-meta {
	border-top:1px Solid #444;
	border-bottom:1px Solid #444;
	color:#919191;
}	
	
.entry-meta a:hover {
	color:#fff;
}	
	
.site-content [class*="navigation"] a {
	color:#fff;
	background:#222;
}	
	
.comment-list li.comment .comment-body {
	padding:18px 18px 0px;
	margin:0 0 0px;
	border-top:1px solid #555;
	border-right:1px solid #555;
	border-left:1px solid #555;
	border-bottom:1px solid #555;
}	

.comment-list li.comment .comment-author .fn {
	color:#0B6791;
	font-size:18px;
	font-family:Oswald,Yanone Kaffeesatz,Arial,Helvetica,sans-serif;
	font-weight:700;
	text-transform:uppercase;
}	
	
.comment-list li.comment .comment-author .fn a {
	color:#fff;
	text-decoration:none;
}	
	
.comment-list li.comment .comment-metadata a:hover {
	color:#fff;
}	
	
.comment-list li.comment .reply a:hover {
	color:#fff;
}	
	
#cancel-comment-reply-link {
	margin:0 0 0 10px;
	color:#999;
	font-size:.9em;
	text-decoration:none;
}	
	
.widget-title {
	margin:0;
	font-size:1.6em;
	text-transform:uppercase;
	letter-spacing:2px;
	border-bottom:1px solid #444;
}	
	
.widget li a {
	font-family:Oswald;
	text-transform:none;
	font-weight:400;
	font-size:13px;
	color:#fff;
}

.widget li {
	padding:6px 0;
	border-bottom:1px solid #444;
}

.site-footer {
	color:#999;
	background:#222;
}

.br-1080p img {
	border:4px solid #000;
	box-shadow:0px 0px 12px 6px #444;
	-webkit-box-shadow:0px 0px 12px 6px #999;
}
.br-720p img {
	border:4px solid #056cba;
	box-shadow:0px 0px 12px 6px #444;
	-webkit-box-shadow:0px 0px 12px 6px #999;
}
.threed img {
	border:4px solid #fff;
	box-shadow:0px 0px 12px 6px #444;
	-webkit-box-shadow:0px 0px 12px 6px #999;
}
.hd-dvd img {
	border:4px solid #87122f;
	box-shadow:0px 0px 12px 6px #444;
	-webkit-box-shadow:0px 0px 12px 6px #999;
}
.sim img {
	box-shadow:0px 0px 12px 6px #444;
	-webkit-box-shadow:0px 0px 12px 6px #777;
}

.imdbRatingPlugin {
	color:#0B6791;
}

.imdbRatingPlugin span.ofTen {
	color:#999;
	font-size:9px
}

.imdbRatingPlugin span.votes {
	font-size:13px;
	color:#aaa;
	margin-left:3px
}

}
