style.css:
@-moz-document domain('kanbanflow.com') {

/*Background*/    
#board-wrapper{background-color: #331a1a !important;}

/*Swimlane Header / Row headers*/
.board-swimlane-header{ background-color:#663333 !important;}
.board-swimlane-name{color:#EC7600;}    

/*Column headers*/
.columnHeader, .columnHeader-inner, .columnHeader-frozen{background-color: #663333 !important;}
.columnHeader-name, .columnHeader-count{color:#EC7600;}

/*Task Headers*/
.board-taskListCellHeader-inner{background-color: #884444;}
.board-taskListCellHeader-taskCount{color:#EC7600 !important}

/*Task Container*/
.board-taskListCell-inner{background-color: #cb9a9a !important;}       

/*Task Group (Date)*/
.task-groupLabel{color:#003380}


}
