style.css:
@-moz-document url("https://www.webpagefx.com/web-design/color-picker/") {
body{
    background:#545454;   
}
body > div {
    background: rgb(44, 44, 44) !important;
}
font div:nth-child(5) {
    background: rgb(44, 44, 44) !important;
}
div:nth-child(6) , font div:nth-child(5) {
    background: rgb(44, 44, 44) !important;
}
#css+ div , div:nth-child(6) , font div:nth-child(5){
    background: rgb(44, 44, 44) !important;
}
div div font table{
    background: rgb(44, 44, 44) !important;
    border: none !important;
}
br+ table td {
    background:#545454;
}
#tips {
    background: rgb(44, 44, 44) !important;
}
#tips font{
    color: white !important;   
}
#tips a {
    color: #949494 !important;   
}
.customColorButton {
    background-color:#808080;
}
.customColorBox {
    background:#4f4f4f;   
}
}
