#appearance-category-nav-items {
	display: none;
	opacity: 0;
	width: 100%;
	height: 300px;
	-webkit-transform: translateY(-20px);
	-ms-transform: translateY(-20px);
	-o-transform: translateY(-20px);
	transform: translateY(-20px);
	position: absolute;
	z-index: -1;
	top: 100%;
	left: 0;
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0.95) 0%,rgba(255, 255, 255, 0.95) 25%,rgba(255, 255, 255, 0.95) 75%,rgba(0, 0, 0, 0.95) 95%);
	border: 1px solid #000000;
	box-shadow: 0 0 10px -6px, inset 0 0 0px 1px #000000;
	margin: 8px 0;
	padding: 10px 0;
	border-radius: 4px;
}

#selectedCategory.open + #appearance-category-nav-items {
	opacity: 1;
	-webkit-transform: translateY(0);
	-ms-transform: translateY(0);
	-o-transform: translateY(0);
	transform: translateY(0);
	-webkit-transition: .3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
	transition: .3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

#appearance-category-nav-items:before,
#appearance-category-nav-items:after {
	content: "";
	position: absolute;
	top: -9px;
	left: 12px;
	width: 0;
	height: 0;
	border-bottom: 6px solid #BBB;
	border-left: 6px solid transparent;
	border-right: 6px solid transparent;
	margin-top: 3px;
}
#appearance-category-nav-items:after {
	margin-top: 4px;
    border-bottom-color: #000000;
}

#appearance-category-nav-items .scroll {
	height: 100%;
	overflow: auto;
}
#appearance-category-nav-items ul,
#appearance-category-nav-items li {
	list-style: none;
	padding: 0;
	margin: 0;
	font-size: 12px;
	line-height: 20px;
	min-height: 20px;
}
#appearance-category-nav-items li {
	display: block;
	padding: 6px 30px 6px 10px;
	color: #f4af0a;
	cursor: pointer;
	position: relative;
	overflow: hidden;
	font-weight: bold;
}
#appearance-category-nav-items li:hover {
	background: #FFFFFF;
	color: #000000;
	font-weight: bold;
}

#appearance-category-nav-items li.selected {
	background: #000000;
	color: #f4af0a;
	font-weight: bold;
}
#appearance-category-nav-items li.selected:after {
	opacity: 1;
    right: 0;
}

#appearance-category-nav-items li:after {
	content: "";
	position: absolute;
	z-index: 3;
	top: 0;
	right: -10px;
	width: 30px;
	height: 100%;
	background: url(data:image/svg+xml;base64,) no-repeat 50% 50%;
	opacity: 0;
	-webkit-transition: .2s;
	transition: .2s;
}

