  color: rgb(244, 175, 10);
}

  /*change la couleur des liens dans la barre du haut, ceux du bouton 'citer' et ceux des pages non sélectionnées, et de tous les liens en général*/
.pun a {
  color: rgb(126, 105, 42);
  text-decoration: none;
}

/*Les petits sous titres comme "réponse rapide ou "résumé des discussions"*/
.pun h2 {
  background-color: rgba(244, 175, 10, 1);
  color: rgb(244, 175, 10);
  display: block;
  font-size: 11px;
  font-weight: 700;
  margin: 0;
  padding: 4px 6px;
}


/*Pour changer la couleur de la box d'écriture du message*/
.pun .form {
  color: #000000;
  font-size: 11px;
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.9) 0%,rgba(244, 175, 10, 0.71) 80%,rgba(255, 0, 0, 0.9) 100%);
}

/*pas compris 8D*/
.pun legend {
  color: #FFFFFF;
  padding: 0 6px;
}


/*pas compris on plus 8D*/
.pun fieldset {
  background-color: rgba(255, 0, 0, 0.03);
  border: solid 1px rgb(131, 105, 147);
  padding: 0 12px;
}

/*changer la couleur des admins partie 2*/
#forum .pun.box>.inbox.group2 .post {
  border-left: solid rgba(172, 84, 69, 1) 120px;
}

.pun .inbox.group2 {
  border: solid 1px #000000;
}

.pun.header {
  padding: 5px;
   background-color: rgba(0, 0, 0, 0.7);
  border-radius: 5px;
  border: 1px solid rgb(244, 175, 10);
}


/************************************************************************/

/*CHASSE AU TRESOR (carte)*/
    /*FRAME*/
#treasurehunt-fold {
    top: 0px;
    border-radius: 6px;
    position: absolute;
    background: rgb(0, 0, 0);
    z-index: 3;
    padding: 0px 18px;
}
/* AIDES */
.tooltip-content {
    display: block;
    position: fixed;
    font-family: "Banda Regular";
    color: white;
    text-shadow: 0px 0px 3px rgba(255,255,255,0.5);
    padding: 10px 10px;
    font-size: 14px;
    background: rgba(0, 0, 0, 0.8);
    border: 2px solid rgb(244, 175, 10);
    border-radius: 3px;
    box-shadow: 0px 0px 4px rgb(0, 0, 0);
    display: none;
    z-index: 10;
    white-space: nowrap;
}
/* LOCATION CARTE */
.mapLocation {
    width: 14px;
    height: 14px;
    position: absolute !important;
    border-radius: 14px;
    border: 2px solid #000000;
    box-shadow: -1px -1px 1px rgb(20, 222, 230), 1px 1px 1px rgb(0,238,248), -1px 1px 1px rgb(0,238,248), 1px -1px 1px rgb(0,238,248);
    /* background: rgb(0, 210, 226); */
    cursor: pointer;
}

}
