style.css:
@-moz-document domain('62.0.192.29'), domain('gitlab.ort.org.il') {
     /* issue body, issue title, comment*/
    .issuable-details .issue-title,
    #issue_title,
    .issuable-details .description,
    #issue_description,
    .md-preview-holder,
    .notes,
                  #note_note{
        direction: rtl;
    }
    /* issue body*/
    .wiki ul, .wiki ol {
        margin: 6px 18px 6px 0 !important;
    }

                /* comment */
                /* code should be ltr */
                .wiki code,
    .md-preview-holder code{
                                direction: ltr !important;;
                                text-align: left !important;;
                }

    /* issues list*/
    ul.issues-list {
        direction: rtl;
    }
    .issues-list .issue .issue-check {
        float: right;
        padding: 0 0 0 8px;
    }
    .pull-right {
        float: left !important;
    }

    .issue-info > span {
        background-color: #e5e5e5;
        color: #3d3d3d;
        padding: 2px 5px;
        display: inline-block;
        border-radius: 3px;
    }
}
