@-moz-document regexp("https?:\/\/(?:www.)?tumblr.com.*") {
/* ↓----------------------[ image ]----------------------↓ */
	/* ----↓ dormant ↓---- */

.elevator-wrapper.visible .elevator:before {content: none!IMPORTANT;}
.elevator-wrapper.visible .elevator[class*="elevator"] {
    background: url("data:image/png;base64,") no-repeat transparent !IMPORTANT;
    height: 50px!IMPORTANT; width: 50px!IMPORTANT;}

	/* ----↑ dormant ↑---- */
	/* ----↓ hover ↓---- */

.elevator-wrapper.visible .elevator[class*="elevator"]:hover {
    background: url("data:image/png;base64,") no-repeat transparent!IMPORTANT;
    height: 50px!IMPORTANT; width: 50px!IMPORTANT;}

	/* ----↑ hover ↑---- */
	/* ----↓ active ↓---- */

.elevator-wrapper.visible .elevator[class*="elevator"]:active {
    background: url("data:image/png;base64,") no-repeat transparent!IMPORTANT;
    height: 50px!IMPORTANT; width: 50px!IMPORTANT;}

	/* ----↑ active ↑---- */
/* ↑----------------------[ image ]----------------------↑ */}
