style.css:
@-moz-document url-prefix("http://totalwar-turkiye.com/") {
body, td, th, tr {color: #3c3c3c !important;}
body {background: #2d0402 url(http://i.hizliresim.com/Z777kG.jpg) no-repeat top center !important; margin-top: 0 !important; padding-top: 0 !important; min-width: 800px !important;}
div#wrapper{width: 100% !important; margin: 0 auto !important;min-width: 780px !important;max-width: 2300px !important;}
#header {background: rgba(255, 0, 0, 0) !important; min-height: 313px !important;}
#header div.frame {background: none !important;display: block;padding: 5px 20px 1em 0; max-height: 313px !important;}  
#main_menu {padding-top: 60px !important; width: 100% !important; font-size: 12px !important;font-weight: bold !important; color: #8e8e8e !important;}
.dropmenu li a.active {background: url(http://i.hizliresim.com/GoooaZ.png) repeat-x top left !important;     min-height: 37px !important;}
.dropmenu li a.active span.firstlevel {color: white !important; background: url(http://i.hizliresim.com/GoooaZ.png) repeat-x top left !important;     min-height: 37px !important;} 
.dropmenu li {margin-top: -10px !important;min-height: 45px !important;} 
.dropmenu li a.firstlevel span.firstlevel{line-height: 34px !important;text-align: center !important;padding: 0px 7px !important;}  
.dropmenu a span{font-size: 12px !important;}

}
