style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jungewelt.de"){
  
  body {
    background: #2f2f2f;
}
  .Wrapper {
    background: #393939;
  }
  .Navi ul li.Active ul li a, .Navi ul li.Active ul li.Active a {
    color: #eb2325;
  }
  div.Main .Section-Header {
    background: #393939;
  }
  h1 a, h2 a, h3 a, h4 a {
    color: #d2d2d2;
}
  .Specials .Box {
    background: #393939;
  }
  div.Main {
    color: #ccc;
  }
  .Article .Infobox {
    color: #ccc;
    background: #404040 none repeat scroll 0% 0%;
  }
  #ID_ArticleAppendix .Stopper {
    background: #393939;
  }
  #ID_ArticleAppendix .Moreinfo .Preview {
    background: #393939 none repeat scroll 0% 0%;
}
  #ID_LoginFormFailed fieldset {
    background: #393939;
  }
  form input[type="submit"] {
    background: #eb2325 none repeat scroll 0% 0%;
  }
  #ID_LoginFormFailed fieldset legend {
    background: #393939 none repeat scroll 0% 0%;
  }

}
