@-moz-document domain("fnnews.com") {
  .sub_ad_sect,
  .ad_header, .tit_banner, .fn_right_side, .fn_right_side2,
  .scroll-to-fixed-fixed.fn_left_side, .fn_left_side, #textLasOut, #mask_box_0, #scrollDiv,
  .fn_news_ad, .clearfix, #dablewidget_AOoRZVoy, .news_cont > div:nth-of-type(12), #ad_body,
   .news_cont > iframe {
    display: none !important;
  }

  div {
    border-width: 0px !important;
    background-color: white !important;
    border-color: white !important;
 }

  .news_cont {
    float: none !important;
    margin: 0 auto !important;
    border: none !important;
  }

  #content {
    background-image: none !important;
  }

  .article {
    color: black;
    font-size: medium !important;
    font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }
}
