style.css:
@-moz-document domain("avabur.com") {

.toprounder, .toprounder15
{
    box-shadow: none !important;
}

#accountActivityTable > .row:nth-child(odd), #clanMemberList tr:nth-child(odd), #clanActivityTable>.row:nth-child(odd), #onlineStaff tr:nth-child(odd), #onlinePlayers tr:nth-child(odd), #myClanDonationTable tr:nth-child(odd), #myClanDonationTable tr:nth-child(odd), .marketListings tr:nth-child(odd), .marketListings tr:nth-child(odd), #rankingTable tr, #milestoneTable tr, .marketListings tr, #inventoryTable tr, #accountReferralTable tr, #fameShopPurchaseTable tr, #clan_list tr:nth-child(odd), .bossParticipantList tr:nth-child(odd), .sorted_table tr:nth-child(odd), .full tr:nth-child(odd), td[class^="calendar-day"]:nth-child(odd)
{
    background: rgba(20, 20, 20, 0.5) !important;
}body {
    background-color: var(--rewamped-body-color);
    background-image: var(--rewamped-body-image);
    background-position: var(--rewamped-body-position);
    background-size: var(--rewamped-body-size);
    background-repeat: var(--rewamped-body-repeat);
    background-attachment: var(--rewamped-body-attachment);
}

h1, h2, h3, h4, h5, h6 {
    background-color: var(--rewamped-header-background-color);
    background-image: var(--rewamped-header-background-image);
}

#accountLinks li {
    border-width: 1px 0 1px 0;
}

table#allThemTables  > tbody > tr > td > table {
    width: 85%;
    margin: 0 7.5%;
}

ul.pagenav > li.page_link.page_cur {
    background-color: var(--rewamped-background-color-light);
}

:root {
    --border-color: var(--rewamped--black-shade-base);
    --border-color-darker: var(--rewamped--black-shade-base);
    --border-color-dark: var(--rewamped-color-dark);
    --border-color-faded: var(--rewamped-color-faded);
    
    --btn-background-color: var(--rewamped-background-color-base);
    --btn-gradient-first-color: var(--rewamped-background-color-light);
    --btn-gradient-second-color: var(--rewamped-background-color-lighter);
    --btn-hover-background-color: var(--rewamped-background-color-light);
    --btn-gradient-hover-first-color: var(--rewamped-background-color-light);
    --btn-gradient-hover-second-color: var(--rewamped-background-color-lighter);
    
    --button-background-color: var(--rewamped-background-color-base);
    --button-gradient-first-color: var(--rewamped-background-color-light);
    --button-gradient-second-color: var(--rewamped-background-color-lighter);
    --button-hover-background-color: var(--rewamped-background-color-light);
    --button-hover-border-color: var(--rewamped-color-darker);
    --button-gradient-hover-first-color: var(--rewamped-background-color-light);
    --button-gradient-hover-second-color: var(--rewamped-background-color-lighter);
    
    --input-button-background-color: var(--rewamped-background-color-base);
    --input-button-gradient-first-color: var(--rewamped-background-color-light);
    --input-button-gradient-second-color: var(--rewamped-background-color-lighter);
    --input-button-hover-background-color: var(--rewamped-background-color-light);
    --input-button-hover-gradient-first-color: var(--rewamped-background-color-light);
    --input-button-hover-gradient-second-color: var(--rewamped-background-color-lighter);
    --input-button-focus-border-color: var(--rewamped-color);
    
    --nav-background-color: #000;
    --nav-gradient-first-color:  var(--rewamped-background-color-base);
    --nav-gradient-second-color: var(--rewamped-background-color-base);
    
    --dropdown-background-color: var(--rewamped-background-color-base);
    --dropdown-border-color: var(--rewamped-color-darker);
    --dropdown-hover-background-color: var(--rewamped-background-color-light);
    
    --nav-hover-background-color: #333;
    --nav-hover-gradient-first-color: var(--rewamped-background-color-lighter);
    --nav-hover-gradient-second-color: var(--rewamped-background-color-lighter);

    --header-background-color: var(--rewamped-transparent-black);
    --header-gradient-first-color: var(--rewamped-color);
    --header-gradient-second-color: var(--rewamped-transparent-black);
    --header-hover-background-color: var(--rewamped-color);
    --header-hover-gradient-first-color: var(--rewamped-background-color-lighter);
    --header-hover-gradient-second-color: var(--rewamped-background-color-lighter);

    --ui-background-color: var(--rewamped-background-color-base);
    --ui-gradient-first-color: var(--rewamped-transparent-black);
    --ui-gradient-second-color: var(--rewamped-transparent-black);
    --modal-close-background-color: var(--rewamped-background-color-base);
    --modal-close-gradient-first-color: var(--rewamped-background-color-lighter);
    --modal-close-gradient-second-color: var(--rewamped-background-color-base);

    --xp-background-color: var(--rewamped-background-color-light);
    --xp-gradient-first-color: var(--rewamped-background-color-lighter);
    --xp-gradient-second-color: var(--rewamped-background-color-light);

    --link-color: var(--rewamped-color);
    --link-hover-color: var(--rewamped-color-lighter);
    
    
    --action-color: var(--rewamped-color);
    --odd-color: var(--rewamped-background-color-base);
    --even-color: var(--rewamped-background-color-lighter);
    --scrollbar-color: var(--rewamped-color);
    --scrollbar-background: var(--rewamped-background-color-light);
    --bubble-background-color: var(--rewamped-background-color-lighter);
    --select-background-color: var(--rewamped-background-color-lighter);
    --input-background-color: var(--rewamped-background-color-lighter);
    
    --calendar-day-background-color: var(--rewamped-background-color-lighter);
    --calendar-day-hover-background-color: var(--rewamped-background-color-light);
    
    --pulsate-color-inner: var(--rewamped-color-lighter);
    --pulsate-color-outer: var(--rewamped-color);
    
    --milestone-complete-color: var(--rewamped-background-color-light);
    --milestone-incomplete-color: var(--rewamped-background-color-base);
    
    --pagination-background-color: var(--rewamped-background-color-base);
    --pagination-border-color-bright: var(--rewamped--black-shade-base);
    --pagination-border-color-dark: var(--rewamped--black-shade-base);
    
    
    --rank-separator-color: var(--rewamped-color-faded);
    --row-highlight-color: var(--rewamped-color-dark);
    
    --slider-color: var(--rewamped-color-darker);

}

/*[[themepick]]*/

}

