style.css:
@-moz-document domain("24tv.ua") {
body, .programs_wrap {
    background-color: #212121;
}
a, .title h5, .title h5 a, .sort_list li a {
    color: #CCC;
}

.news-list li.best .time, .news-in-main-page .author a, .news-list li.best .news-title {
    color: rgb(159, 151, 116);
}

.news-list li.best .news-title:hover {
    color: rgba(159, 151, 116, 0.5);
}

a:focus, a:hover {
    color: rgba(159, 151, 116, 0.5);
}

.date-divider {
    background-color: #686954;
}

.general_news{
    display: none;
}

header{
    background-color: #171717;
}

.b_search .form-text {
    border-bottom: 1px solid #9c9c9c;
    border-left: 1px solid #9c9c9c;
    border-right: none;
    border-top: none;
    color: #CCC;
    background-color: transparent;
}

.best_news .big .news_desc {
    color: #8E8E8E;
}
}
