/* Button styles with Ripple (https://github.com/mladenplavsic/css-ripple-effect) */
.btn,
.subnav-item,
.state,
.btn-sm,
.hx_reponav a.reponav-item,
.social-count,
.Header-link,
.dropdown-item,
.select-menu-item {
	box-shadow: 0 1px 2px rgba(0, 0, 0, .06), 0 1px 1px rgba(0, 0, 0, .12) !important;
	text-decoration: none;
	text-align: center;
	letter-spacing: 0;
	transition: .2s ease-in;
	cursor: pointer;
	position: relative;
	overflow: hidden;
	transform: translate3d(0, 0, 0);
}

.btn:after,
.subnav-item:after,
.state:after,
.btn-sm:after,
.hx_reponav a.reponav-item:after,
.social-count:after,
.Header-link:after,
.dropdown-item:after,
.select-menu-item:after {
	content: "";
	display: block;
	position: absolute;
	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
	pointer-events: none;
	background-image: radial-gradient(circle, #888 10%, transparent 10.01%);
	background-repeat: no-repeat;
	background-position: 50%;
	transform: scale(10, 10);
	opacity: 0;
	transition: transform .5s, opacity 1s;
}

.btn-primary:after {
	background-image: radial-gradient(circle, #fff 10%, transparent 10.01%);
}

.btn:active:after,
.subnav-item:active:after,
.state:active:after,
.btn-sm:active:after,
.hx_reponav a.reponav-item:active:after,
.social-count:active:after,
.Header-link:active:after,
.dropdown-item:active:after,
.select-menu-item:active:after {
	transform: scale(0, 0);
	opacity: .3;
	transition: 0s;
}

input.btn {
	padding: 6px 8px;
}

.btn-primary,
.btn-primary:hover {
	background-image: linear-gradient(#66BB6A, #4CAF50);
}

