/* PR Button fix - No ripple, unfortunately */
.js-mrege-method-menu-button::after,
.select-menu-button::after {
	transform: unset !important;
	opacity: unset !important;
	top: unset;
	left: unset;
	width: unset;
	height: unset;
	display: inline-block;
	position: relative;
}

/* PR Selects fix */
.select-menu-list .select-menu-item {
	display: block;
}

/* Fix shadow on some elements */
.hx_reponav a.reponav-item,
.dropdown-item {
	box-shadow: unset !important;
}

.dropdown-item {
	text-align: unset;
	display: block;
}

/* Fix for dialogs shadow */
summary.btn.btn-danger.boxed-action {
	transform: unset;
}

/* Extra Inputs */
.form_control {
	border-color: revert !important;
}

.form_group > label {
	top: 31px;
	left: 15px;
	position: relative;
	padding: 0px 5px 0px 5px;
	font-size: 1.1em;
	transition: 0.2s;
	pointer-events: none;
}

.form_group.label-animate > label {
	background-color: white;
	top: 12px !important;
	left: 16px !important;
	font-size: 0.9em;
}

.form_control:focus {
	background-color: unset !important;
	border: revert;
	border-color: #2c3e50 !important;
}

/* Fix header search bar */
