style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.desdelinux.net") {
  .td-main-content-wrap, .td-category-grid{
    background-color: #000;
  }
  h1 > a, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a{
    color: #fff;
  }
  .widget a:hover {
    color: #FF0000;
  }
  a{color: #FF0000;}
  .links-w a{background: #FF0000;}
  .widget a{color: white;}
  .recent-comment-text{color: white;}
  .recent-comment-author{color:white;}
  .page-nav a, .page-nav span{color:white;}
  .block-title > span, .block-title > a, .block-title > label, .widgettitle, .widgettitle::after, .td-trending-now-title, .td-trending-now-wrapper:hover .td-trending-now-title, .wpb_tabs li.ui-tabs-active a, .wpb_tabs li:hover a, .td-related-title .td-cur-simple-item, .woocommerce .product .products h2, .td-subcat-filter .td-subcat-dropdown:hover .td-subcat-more{background-color: red;}
  .block-title, .td-related-title, .wpb_tabs .wpb_tabs_nav, .woocommerce div.product .woocommerce-tabs ul.tabs::before{border-color: red;}
}
