style.css:
@-moz-document domain("calq.io") {
/* Big ombobox */

.title.bookmarks .control-group .controls>.select2-container {
    width: 450px;
}
.select2-results {
    max-height: 450px;
}
.select2-drop,
.dropdown-menu {
    border-style: solid;
    border-color: #cccccc;
    border-width: 1px;
    background-color: #fdfdfd !important;
}

/* White background */

.themebgcolor,
.box,
#wrap>.container-fluid,
body {
    background-color: #ffffff !important;
}

.glass {
    background-color: #f4f4f4;
}
.nvc-axis-shade {
    fill: #ffffff;
}
.chart .nv-discreteBarWithAxes .nv-x .tick text,
svg text {
    fill: #444444;
}


