style.css:
@-moz-document domain("budgetlightforum.com") {
hr {
    display: none !important;
}

#page {
    background-color: #333 !important;
}

.views-table {
    width: 100% !important;
}

/*div.forum-topic-header {
    display: flex;
}*/

/* page number links at bottom and top */
div.pager {  /* put pager into same row of top bar if there's room */
    float: right;
    padding: 6px 20px 6px 6px;
}
ul.pager {
    margin: 0 !important;
    padding: 0 !important;
    /* border: 1px solid #f0f !important; */
    float: right;
}
ul.pager li {
    margin: 0 3px 0 3px !important;
    padding: 0 !important;
}
li.pager-first a, li.pager-previous a, li.pager-item a, li.pager-ellipsis, li.pager-next a, li.pager-last a {
    background: transparent !important;
}
li.pager-current {
    color: #ff0040 !important;
}

li.markasread.first.last a img {
    display: none !important;
}

/* Explanation about how email notifications work.  (my subscriptions page) */
div.view-header p, div.view-header p span {
    /* display: none; */
    text-align: left !important;
    font-size: 80% !important;
}

img[src*="feed.png"] {
    width: 0 !important;
    height: 16px !important;
    padding-left: 16px !important;
    background-repeat: no-repeat !important;
    background-image: url("http://i.imgur.com/wWLeU.png") !important;
}

th.views-field.views-field-type {
    display: none !important;
}

td.views-field, div.view-header {
    color: #888;
}

td.views-field.views-field-type {
    display: none !important;
}

body {
    background-color: #222 !important;
    color: #888;
}

span.marker {
    color: #9e0000 !important;
    font-variant: small-caps !important;
}

ul.tabs.secondary {
    border-style: none !important;
    /* background-color: #333 !important; */
    background: linear-gradient(#333, #222) !important;
    border: 1px solid #333 !important;
    border-bottom-left-radius: 5px !important;
    border-top-right-radius: 5px !important;
    padding-left: 0 !important;
    margin-left: 0 !important;
    margin-right: 1px !important;
}

ul.tabs.primary {
    /* border-color: #adadad !important; */
    border-style: none !important;
    height: 0 !important;
    margin-bottom: 20px !important;
}

/* "Write new message" in private messages */
ul.action-links {
    /* display: inline-block !important; */
    float: right !important;
}

img[src*="forum-folder-new-posts.png"] {
    width: 0 !important;
    height: 16px !important;
    padding-left: 16px !important;
    background-repeat: no-repeat !important;
    background-image: url("http://i.imgur.com/fdfDH.png") !important;
}

th {
    background: #3e3e3e;
    border-bottom: 1px solid #555;
}

table {
    border: none !important;
}

th.forum-last-post {
    padding: 0 !important;
}

th.forum-posts {
    padding: 0 !important;
}

th.forum-num-topics {
    padding: 0 !important;
}

th.forum-name {
    padding-left: 0 !important;
}

div#forum-statistics {
    background: url(http://budgetlightforum.com/sites/all/themes/newsflash/images/blue/MastBG.png) !important;
    border-radius: 6px;
    margin-bottom: 10px;
}

#forum-statistics-header {
    background: transparent !important;
    color: #eee;
    text-shadow: 0px 0px 6px #000, 0 0 12px #000 !important;
}

div.forum-statistics-sub-header {
    background: transparent;
    color: #888 !important;
    text-shadow: 0px 0px 6px #000, 0 0 12px #000 !important;
}

div.forum-statistics-sub-body {
    background: #222;
}

div.post-title {
    border-bottom: 1px solid black !important;
}

div.author-pane-inner {
    font-size: 75%;
    line-height: 90%;
}

/* user avatar wrapper */
div.picture img, div.privatemsg-author-avatar img, div.profile div.user-picture img {
    background-color: transparent !important;
    background: linear-gradient(-45deg, #292929,#555) !important;
    border: 1px outset #555 !important;
    border-radius: 6px;
    padding: 6px 7px 7px 6px !important;
    margin: 0px !important;
}

/* Right-align avatars in private messages */
div.privatemsg-author-avatar {
    position: relative;
    min-height: 106px;
    margin: 0px;
    padding: 0px;
}
div.privatemsg-author-avatar div.user-picture {
    position: absolute;
    width: 100px;
    right: 0px;
    padding: 0px;
    margin: 0px;
}

/* position the avatar while looking at someone's profile */
div.profile div.user-picture {
    float: right !important;
    margin: 0;
    padding: 0;
}
div.content div.profile h3 {
    margin-top: 0px !important;
}

div.author-pane-line.author-online {
    background: url("http://i.imgur.com/vdonQ.png") no-repeat !important;
}

div.author-pane-line.author-offline {
    background: url("http://i.imgur.com/mtD6X.png") no-repeat !important;
}

/* "Send PM" link */
a.author-pane-link {
    background: linear-gradient(#57b, #235) !important;    
}

li.comment_reply a img {
    display: none !important;
}

/* Edit, quote, reply, other buttons per post */
a.af-button-small, a.af-button-large {
    background: linear-gradient(#57b, #235) !important;
}

a.af-button-large span, a.af-button-small span {
    color: #ddd;
}

a.flag.flag-action.flag-link-toggle.flag-processed {
    font-size: 15px !important;
    color: #c9c9c9 !important;
    text-transform: lowercase !important;
    font-variant: small-caps !important;
    float: left !important;
    font-weight: normal !important;
}

/* Show Link (from Legend of Zelda) after each external link. */
span.ext {
    background:url(http://toykeeper.net/torches/css/link2.png) 2px center no-repeat !important;
    width: 1px !important;
    height: 1px !important;
    padding-right: 17px !important;
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}
span.ext:hover {
    background:url(http://toykeeper.net/torches/css/link3.png) 3px center no-repeat !important;
    width: 1px !important;
    height: 1px !important;
    padding-right: 17px !important;
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}

img[src*="post-reply.png"] {
    width: 0 !important;
    height: 32px !important;
    padding-left: 32px !important;
    background-repeat: no-repeat !important;
    background-image: url("http://i.imgur.com/tQz5x.png") !important;
}

h1.title {
    color: #eee !important;
    font-size: 16pt !important;
    /* allow thread title to wrap (FIXME: doesn't work) */
    float: none;
    display: inline;
    word-wrap: normal !important;
    white-space: normal !important;
    vertical-align: top;
}

div.breadcrumb a {
    color: #0094d9 !important;
    margin-left: 10px !important;
}

/* Forum Categories table */
div.forum-table-superheader {
    /* background: linear-gradient(#57b, #235) !important; */
    background: url(http://budgetlightforum.com/sites/all/themes/newsflash/images/blue/MastBG.png) !important;
    border-radius: 6px 6px 0 0;
    margin-top: 10px;
}
div.forum-table-name a {
    color: #eee;
    text-shadow: 0px 0px 6px #000, 0 0 12px #000 !important;
}
div.forum-table-description {
    color: #888;
    text-shadow: 0px 0px 5px #000, 0 0 10px #000 !important;
}

/* Forum Categories table details */
div.forum-description {
	color: #888;
}

#forum .forum-table, #forum .forum-topics {
    border: 0px solid #91CDF5 !important;
}

.topics, .last-reply {
    background-color: #222 !important;
    color: #777;
}

.num.posts {
    background-color: #111 !important;
    color: #666;
}
div.topic-post-count {
    color: #888;
}

div {
    border: none !important;
}

/* configuring each forum post block */

div.forum-post {
    color: #ccc;
    background-color: #444 !important;
    border-radius: 6px !important;
    margin-top: 10px;
}

/* forum post header line */
div.forum-post-info {
    background: linear-gradient(#57b, #235) !important;
    padding: 3px 2px 3px 3px;
    border-top-left-radius: 6px !important;
    border-top-right-radius: 6px !important;
    border-top-width: 1px !important;
    border-left-width: 1px !important;
    border-right-width: 1px !important;
    border-style: none !important;
}

span.new {
    color: #000;
    font-size: 125%;
    text-shadow: 0 0 5px #f00 !important;
    margin-left: 6px;
}

/* Private messages */
div.privatemsg-message-body {
    color: #ccc;
}

/* "New" label in a private thread */
span.new.privatemsg-message-new {
    margin-right: 10px;
    color: #000;
    font-size: 125%;
    text-shadow: 0 0 5px #f00 !important;
}
/* Old private messages */
div.privatemsg-message div.privatemsg-message-column {
    /* background: linear-gradient(to right, #2c2c2c, #333); */
    background-color: #2c2c2c;
    padding: 6px 0px 2px 10px;  /* for some reason, padding the right causes a layout issue */
    border-radius: 6px;
}
/* New private messages */
/* div.privatemsg-message-new div.privatemsg-message-information { */
/* div.privatemsg-message-new div.privatemsg-message-body { */
div.privatemsg-message-new div.privatemsg-message-column {
    background: linear-gradient(#333, #4a4a4a);
    border: 1px solid #4a4a4a !important;
}
/* make the message count line stop wrapping */
/* can't override the narrow width for some reason, but I can make it not a block */
div.privatemsg-view-pager {
    color: #888;
    display: inline !important;
}

div.forum-post-panel-sub {
    border-bottom-left-radius: 20px !important;
    border-top-right-radius: 20px !important;
    margin-top: 5px !important;
    margin-bottom: 5px !important;
    margin-left: 5px !important;
