}

/* give my posts a special background image */
div.posted-by-6266 div.forum-post-panel-main {
    background: url(http://toykeeper.net/torches/avatars/tk-post-bg.png) no-repeat top right !important;
}

/* 3 (and deeper)-level deep quotes */
blockquote.quote-msg blockquote.quote-msg blockquote.quote-msg  {
    border-color: #666 !important;
    background-color: #111 !important;
    color: #444;
}
/* 2-level deep quotes */
blockquote.quote-msg blockquote.quote-msg {
    border-color: #888 !important;
    background-color: #222 !important;
    color: #666;
}
/* quoted text (all, base style) */
blockquote.quote-msg {
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #aaa !important;
    border-radius: 6px;
    background-color: #333 !important;
    color: #999;
    margin-left: 20px;
    margin-right: 20px;
    margin-top: 5px;
    margin-bottom: 5px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 6px;
    padding-bottom: 6px;
}

div.quote-author {
